css:
@-moz-document domain("discussions.apple.com") {
body,
.alt-light,
.persist-question {
    background-color: #333;
}

#globalfooter-wrapper,
.code-source {
    filter: invert(80);
}

.contenttable-title,
.contenttable-section-title,
p,
h2,
h1,
#ac-globalnav .ac-gn-link,
#ac-gn-link-search,
.localnav-title,
.localnav-title > a,
.localnav-menu-link,
#content-post-title,
.locked-thread-message,
.subnav-title,
.contact-support,
.nav,
.sub-nav-link,
.author-name,
.persist-question-text {
    color: azure !important;
}

#ac-globalnav {
    background-color: #444;
}

#ac-globalfooter {
    background-color: #222;
    color: azure !important;
}
}
