css:
@-moz-document domain("bilibili.com") {
.video-control-show .bui-thumb-dot-special{
    -webkit-transform: scale(1) !important;
    transform: scale(1) !important;
    opacity: 1 !important;
}
.bilibili-player-video-progress .bpui-slider-progress, .bilibili-player-area.video-control-show .bilibili-player-video-progress-slider .bui-bar-normal {
        background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAMCAYAAACji9dXAAAAG0lEQVQImWP4+/nWfyYGBgYGJqY7dxggLCIJAC6UBZpDm4I9AAAAAElFTkSuQmCC") repeat-x !important;
 
    }
    .bilibili-player-video-progress .bilibili-player-video-progress-buffer-range, .bilibili-player-area.video-control-show .bilibili-player-video-progress-slider .bui-bar-buffer {
        background: url("data:image/png;base64,") !important;
    }
    .bui-track-video-progress .bui-thumb .bui-thumb-dot-special div svg {
        display: none;
    }
    .bilibili-player-video-progress .bpui-slider-handle {
        margin-left: -17px !important;
        margin-top: -4px !important;
    }
    .bui-track-video-progress .bui-thumb .bui-thumb-dot-special div {
        margin-left: -8px !important;
        margin-top: -4px !important;
    }
    .bilibili-player-video-progress .bpui-slider-handle, .bui-track-video-progress .bui-thumb .bui-thumb-dot-special div {
        background: url("data:image/png;base64,") !important;
        width: 34px !important;
        height: 40px !important;
        border: none !important;
        box-shadow: none !important;
        margin-left: -18px !important;
        margin-top: -20px !important;
        transform: scale(0.8) !important;
        -webkit-transform: scale(0.8) !important;
        -moz-transform: scale(0.8) !important;
        -ms-transform: scale(0.8) !important;
        transition: transform .1s ease;
    }
    .bilibili-player .bilibili-player-area .bilibili-player-video-control .bui-slider .bui-track.bui-track-video-progress {
        height: 8px;
    }
    .bilibili-player .bilibili-player-area .bilibili-player-video-control .bilibili-player-video-progress.bilibili-player-show .bui-track.bui-track-video-progress {
        height: 8px
    }
}
