@-moz-document domain('procyclingstats.com') {
/* ------------------------------------------------------------------------------------------- */
/* Procyclingstats.com - Darkmode
/* by jaf
/* ------------------------------------------------------------------------------------------- */

/* ------------------------------------------------------------------------------------------- */
/* Color scheme - FF Midnight - Galaxy
/* ------------------------------------------------------------------------------------------- */

:root {
  --ff-midnight-color-base0:         #EAEDF1;
  --ff-midnight-color-base5:         #DEE4EA;        
  --ff-midnight-color-base10:        #D0D6DC;
  --ff-midnight-color-base30:        #929AA7;
  --ff-midnight-color-base50:        #5F6674;
  --ff-midnight-color-base70:        #525966;
  --ff-midnight-color-base80:        #494F5A;
  --ff-midnight-color-base90:        #3F454F;
  --ff-midnight-color-base92:        #373C48;
  --ff-midnight-color-base95:        #2D323D;
  --ff-midnight-color-base:          #272C35; /* main theme color */
  --ff-midnight-color-base110:       #21252C;
  --ff-midnight-color-base120:       #181A20;

  --ff-midnight-color-accent-light:  #B7D1FF;
  --ff-midnight-color-accent:        #88B0F8; 
  --ff-midnight-color-accent-dark:   #588BE8;
  --ff-midnight-color-accent-darker: #4871BD;
    
  --dm-red: #ec5d4e;
  
  --base-font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, tahoma, verdana, arial, sans-serif;
}

/* ------------------------------------------------------------------------------------------- */
/* Base styling and Homepage
/* ------------------------------------------------------------------------------------------- */

.red {
  /* color: #ec3838; */
  color: var(--dm-red);
}


/* pcs_v178.css | https://www.procyclingstats.com/pcs_v178.css */

html, body {
  font: 12px var(--base-font-family);
  background: var(--ff-midnight-color-base);
  color: var(--ff-midnight-color-base30);
}

a:link, a:visited {
  color: var(--ff-midnight-color-base30);
}

a.view_more:link, 
a.view_more:visited {
  background: var(--ff-midnight-color-base90);
}

a.view_more:hover, 
a.view_more:active {
  background: var(--ff-midnight-color-base80);
  color: var(--ff-midnight-color-base10);
  text-decoration: none;
}


.content h1 {
  font: 600 25px var(--base-font-family);
}

.content h2 {
  color: var(--ff-midnight-color-base30);
  font: 600 21px var(--base-font-family);
}

.content h3 {
  font: bold 14px var(--base-font-family);
  padding: 7px 0 4px;
}

.entry h1 {
  font-weight: 600;
  letter-spacing: -0.5px;
}

.header.v2 {
  background: var(--ff-midnight-color-base120);
}

.nav_v2 li a.reg {
  color: var(--ff-midnight-color-base30);
  background: var(--ff-midnight-color-base120);
}

.nav_v2 li a.reg:hover {
  background: var(--ff-midnight-color-base110);
}

.nav_v2 li a.more {
  background: var(--ff-midnight-color-base120);
  color: var(--ff-midnight-color-base80);
}

.nav_v2 li a.more:hover {
  background: var(--ff-midnight-color-base);
  color: var(--ff-midnight-color-base10);
}

.nav_v2 li {
  border-right: 1px solid var(--ff-midnight-color-base95);
}

.nav_v2 li:hover a.more {
  background: var(--ff-midnight-color-base110);
  color: var(--ff-midnight-color-base30);
}

.nav_v2 li.crimson:hover a.more {
  background: rgba(0,0,0,.15);
}

.nav_v2 li.crimson a.reg:hover, 
.nav_v2 li.crimson span ul li a:hover, 
.nav_v2 li.crimson a.more:hover {
  color: var(--ff-midnight-color-base0);
}

.nav_v2 li.crimson a.reg, 
.nav_v2 li.crimson a.more, 
.nav_v2 li.crimson span {
  color: var(--ff-midnight-color-base0);
}

.nav_v2 li a.reg {
  font: 15px var(--base-font-family);
}

.nav_v2 li.crimson li a {
    color: var(--ff-midnight-color-base0);
}

/* main nav dropdown */

.nav_v2 li span {
  background: var(--ff-midnight-color-base110);
}

.nav_v2 li span ul li a {
  color: var(--ff-midnight-color-base30);
}

.nav_v2 li span ul li a:hover {
  background: var(--ff-midnight-color-base120);
  color: #4899da;
}

/* results page - nav dropdown popups */

.pagenav li a:link, 
.pagenav li a:visited {
  color: var(--ff-midnight-color-base10);
}

.pagenav li a:hover, 
.pagenav li a:active {
  background: var(--ff-midnight-color-base80);
}


.pagenav li.cur a {
  background: var(--ff-midnight-color-base120);
  color: var(--ff-midnight-color-base10);
}

.pagenav li ul li a:link, 
.pagenav li ul li a:visited {
  background: var(--ff-midnight-color-base120);
  color: var(--ff-midnight-color-base30);
}

.pagenav li ul li a:hover, 
.pagenav li ul li a:active {
  background: var(--ff-midnight-color-base110);
  color: var(--ff-midnight-color-base0);
}

.pagenav li ul {
  background: var(--ff-midnight-color-base120);
  border: 1px solid var(--ff-midnight-color-base120);
}


.lrFilter:link, 
.lrFilter:visited {
  color: var(--ff-midnight-color-base30);
}

.homeTbl1.black table td.red a:link, 
.homeTbl1.black table td.red a:visited {
  color: var(--dm-red);
}


/* results filter input */

.filter li span.input input[type="text"] {
  padding: 5px;
  border: 0;
  background: var(--ff-midnight-color-base50);
  border-radius: 4px;
}

.filter li span.input input[type="text"]:focus {
  background: var(--ff-midnight-color-base30);
}

/* Invert icon color ----------------------------------- */

.usageCS {
  filter: invert(100);
}

a.usageCS.blue:link, a.usageCS.blue:visited {
  color: orange;
}

/* SEARCHFORM HEADER ------------------------------------------------------------------------------------------- */

.searchform {
  padding: 5px;
  font: 14px var(--base-font-family);
  border: 0;
  border-radius: 3px;
  background: var(--ff-midnight-color-base70);
  color: var(--ff-midnight-color-base110);
}

.searchform:focus {
  background: var(--ff-midnight-color-base30);
}

.searchSubmit {
  filter: saturate(0) invert(90%);
}

.searchtitle {
  font: 14px var(--base-font-family);
  color: var(--ff-midnight-color-base50);
}


/* LANGUAGE NAV ------------------------------------------------------------------------------------------- */

.language2 {
  color: var(--ff-midnight-color-base30);
  margin-top: 15px;
}

.language2 li ul {
  border: 1px solid var(--ff-midnight-color-base90);
  background: var(--ff-midnight-color-base120);
  border-radius: 4px;
}

.language2 li ul a {
  display: block;
}



/* MOBILE NAV ------------------------------------------------------------------------------------------- */

@media screen and (max-width: 700px) {
  .topper {
    background: var(--ff-midnight-color-base120);
    box-shadow: 0 0 4px 2px var(--ff-midnight-color-base90);
  }
}

.mobnav li a:link, 
.mobnav li a:visited {
  border: 1px solid var(--ff-midnight-color-base70);
}

.mobnav ul {
  background: var(--ff-midnight-color-base120);
  border: 1px solid var(--ff-midnight-color-base90);
}



/* SCROLL TO TOP LINK ------------------------------------------------------------------------------------------- */

.gototop:link, .gototop:visited {
  position: absolute;
  top: 10px;
  right: 10px;
}



/* FOOTER ------------------------------------------------------------------------------------------- */


.footer {
  background: var(--ff-midnight-color-base110);
}

.ftr-list a:link, .ftr-list a:visited {
  color: var(--ff-midnight-color-base30);
}

.ftr-list li h3 {
  color: var(--ff-midnight-color-base50);
}

.subfooter {
  background: var(--ff-midnight-color-base120);
}

.subfooter .sf {color: var(--ff-midnight-color-base50);}


/* TABLES ------------------------------------------------------------------------------------------- */

.basic td {
  border-right: 1px solid var(--ff-midnight-color-base);
  border-bottom: 1px solid var(--ff-midnight-color-base95);
  color: var(--ff-midnight-color-base30);
}

.basic td:last-child {
  border-right: 0;
}


.basic th {
  border-bottom: 3px solid var(--ff-midnight-color-base90);
  color: var(--ff-midnight-color-base30);
  font: 600 12px var(--base-font-family);
}


.basic tr:hover {
  background: var(--ff-midnight-color-base95);
}

.basic tr:hover td {
  border-right: 1px solid var(--ff-midnight-color-base95);
}

.basic thead tr:hover {
  background: transparent;
}


/* home sidebar - upcoming games box */
.basic div[style*="color: #777"] {
  color: var(--ff-midnight-color-base50) !important;
}



.homeTbl1 {
  border: 0;
  padding: 10px;
  border-radius: 5px;
  background: var(--ff-midnight-color-base110);
}

.homeTbl1 table th {
  color: var(--ff-midnight-color-base50);
  font-weight: 600;
}

.homeTbl1 tr td {
  border-right: 1px solid var(--ff-midnight-color-base110);
}


.homeTbl1.black {
  background-color: var(--ff-midnight-color-base110);
  color: var(--ff-midnight-color-base30);
}

.homeTbl1.black table th {
  background-color: inherit;
  border-bottom: 3px solid var(--ff-midnight-color-base90);
}

.homeTbl1.black tr td {
  border-bottom: 1px solid var(--ff-midnight-color-base95);
  color: var(--ff-midnight-color-base30);
}

.homeTbl1.black tr:hover td {
  background-color: var(--ff-midnight-color-base95);
  border-right: 0;
}

.homeTbl1.black table td a:link, 
.homeTbl1.black table td a:visited {
  color: var(--ff-midnight-color-base30);
}

.homeTbl1.black table td a:hover, 
.homeTbl1.black table td a:active {
  text-decoration: underline;
}

.homeTbl1.black a.goto:link, 
.homeTbl1.black a.goto:visited {
  background: var(--ff-midnight-color-base90);
  color: var(--ff-midnight-color-base30);
}

.homeTbl1 a.goto:link, 
.homeTbl1 a.goto:visited {
  background: var(--ff-midnight-color-base90);
}



/* RESULTS ------------------------------------------------------------------------------------------- */


.main-results .top3 {
  color: var(--ff-midnight-color-base30);
}

.main-results li {
  border-top: 1px solid var(--ff-midnight-color-base90);
}

.situLiveCont {
  border: 1px solid var(--ff-midnight-color-base90);
}

.main-results .keyinfo,
.main-results .keyinfo span {
  color: var(--ff-midnight-color-base50) !important;
}

.main-results .keyinfo {
  color: var(--ff-midnight-color-base50);
}

.main-results li .img.nophoto {
  border: 1px solid var(--ff-midnight-color-base70);
  color: var(--ff-midnight-color-base30);
}

/* change inline style color */
span[style*="#e00"] {
  color: var(--dm-red) !important;
}

table span[style*="#000"] {
  color: var(--ff-midnight-color-base30) !important;
}

.tag {
 color: var(--ff-midnight-color-base30);   
}


/* results Time Trials */

.resTTTr a:link, .resTTTr a:visited {
  color: var(--ff-midnight-color-base30);
}

.resTTTb a:link, .resTTTb a:visited {
  color: var(--ff-midnight-color-base30);
}

.tttRidersCont {
  border-bottom: 1px solid var(--ff-midnight-color-base90);
}

.resTTTh {
  border-bottom: 3px solid var(--ff-midnight-color-base90);
}

.extraColsOptions div {
 color: var(--ff-midnight-color-base30);
}


/* Stage + year dropdown */

.ESNav select {
  border: 0px solid var(--ff-midnight-color-base30);
  padding: 4px;
  border-radius: 5px;
}



/* RESULTS TABS ------------------------------------------------------------------------------------------- */

.restabs li.cur {
  border-bottom: 1px solid var(--ff-midnight-color-base90);
  background: var(--ff-midnight-color-base90);
}

.restabs li.cur a:link, .restabs li.cur a:visited {
  color: var(--ff-midnight-color-base30);
}

.restabs li {
  margin-left: 0px;
  margin-right: 4px;
  background: var(--ff-midnight-color-base);
  border: 0;
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
}

.restabs {
  border-bottom: 3px solid var(--ff-midnight-color-base90);
}

.restabs li a:link, .restabs li a:visited {
  padding: 6px 8px 3px;
  color: var(--ff-midnight-color-base30);
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  min-width: 40px;
  text-align: center;
}

.restabs li a:hover, .restabs li a:active {
  background: var(--ff-midnight-color-base80);
}

/* fix tab icon stage results */

.restabs li a span {
  height: 14px;
}

.restabs li a span.st4 {
  background-position: -180px -20px;
}

.restabs li a span.st5 {
  background-position: -200px -20px;
}

.restabs li a span.st6 {
  background-position: -240px -20px;
}

.restabs li a span.st7 {
  background-position: -220px -20px;
}

.restabs li a span.st10 {
  background-position: -260px -20px;
}


/* fix home shirt icons */


.shirt.st7.w12,
.shirt.st10.w12,
.shirt.st6.w12, 
.shirt.st5.w12,
.shirt.st4.w12 {
  height: 10px;
}



/*  RESULTS SIDEBAR ------------------------------------------------------------------------------------------- */



.tree3 li ul li a:hover, .tree3 li ul li a:active, .tree3 li ul li.cur a {
  color: var(--ff-midnight-color-base30);
}

.tree3 li ul li a:link, .tree3 li ul li a:visited {
  color: var(--ff-midnight-color-base30);
}

.tree3 li span {
  background: var(--ff-midnight-color-base110);
  font-weight: 600;
}

.tree3 li ul {
  list-style: disc;
}


/*  TABS ------------------------------------------------------------------------------------------- */

.list.table li {
  border-bottom: 1px solid var(--ff-midnight-color-base95);
}

.list.table li.thead {
  border-bottom: 3px solid var(--ff-midnight-color-base90);
}

.stat-tabs li a:link, .stat-tabs li a:visited {
  color: var(--ff-midnight-color-base30);
}

.stat-tabs li {
  border: 1px solid var(--ff-midnight-color-base90);
}

.stat-tabs {
  border-bottom: 3px solid var(--ff-midnight-color-base90);
}

.stat-tabs li.cur {
  border-bottom: 1px solid var(--ff-midnight-color-base80);
  background: var(--ff-midnight-color-base80);
}

.stat-tabs li.cur a:link, 
.stat-tabs li.cur a:visited {
  color: var(--ff-midnight-color-base0);
  font-weight: 600;
  text-decoration:none;
}

/*  RACE OVERVIEW ------------------------------------------------------------------------------------------- */

span[style*="width: 210px;"] {
  width: 205px !important;
}


/*  RIDER OVERVIEW ------------------------------------------------------------------------------------------- */


.horiztree {
  border-bottom: 0;
  margin: 4px 0 10px 0;
}

.horiztree li {
  margin-right: 1px;
  height: auto;
  border: 0;
  background: var(--ff-midnight-color-base90);
  color: var(--ff-midnight-color-base0);
}

.horiztree li:hover {
  background: var(--ff-midnight-color-base80);
}

.horiztree li.cur {
  border-bottom: 0;
  background: var(--ff-midnight-color-base30);
}

.horiztree li a:link, 
.horiztree li a:visited {
  padding: 7px 7px;
  color: var(--ff-midnight-color-base10);
}

.horiztree li a:hover, 
.horiztree li a:active {
  color: var(--ff-midnight-color-base10);
  background: var(--ff-midnight-color-base80);
}

.horiztree li.cur a:hover, 
.horiztree li.cur a:active {
  color: var(--ff-midnight-color-base120);
  background: var(--ff-midnight-color-base10);
}

.horiztree li span {
  padding: 7px;
}

.horiztree li ul {
  border: 1px solid var(--ff-midnight-color-base80);
}

.horiztree li:first-child,
.horiztree li:first-child a {
  border-top-left-radius:4px;
  border-bottom-left-radius:4px;
}


.horiztree li:last-child,
.horiztree li:last-child a {
  border-top-right-radius:4px;
  border-bottom-right-radius:4px;
}


.prres li ul li {
  border-top: 1px solid var(--ff-midnight-color-base95);
}

.prres li {
  border-top: 3px solid var(--ff-midnight-color-base90);
}

.prres li a:link, .prres li a:visited {
  color: var(--ff-midnight-color-base30);
}

.prres li:hover {
  background: none;
}

.prres li ul li:hover {
  background: var(--ff-midnight-color-base95);
}

.ranking-per-season li {
  border-top: 1px solid var(--ff-midnight-color-base95);
}

.rps-points {
  color: var(--ff-midnight-color-base30);
}

.rps-pos {
  color: var(--ff-midnight-color-base30);
}

.ranking-per-season li:hover {
  background: var(--ff-midnight-color-base);
}

.ranking-per-season li:hover div.bar div.bg {
  background: #6ad45f;
}

.key-stats li div:nth-child(1) {
  background: #6ad45f;
  color: var(--ff-midnight-color-base110);
  font-weight: 600;
}

.key-stats li span {
  color: var(--ff-midnight-color-base50);
}


.ranking-per-season li div.bar div.bg {
  background: #6ad45f;
}

.rdrStandings span {
  background: var(--ff-midnight-color-base120);
}

.seasonResults:hover {
  color: var(--ff-midnight-color-base0);
}

.rdr-program li {
  border-top: 1px dashed var(--ff-midnight-color-base80) !important;
}

.rdr-img-cont img {
  border-radius: 8px;
}

.detailsImg {
  max-height: 140px;
  margin: 0 5px 1px 0;
  border-radius: 12px;
}

/* .palmares */
span[style*="color: #777; font: 11px tahoma"] {
  color: var(--ff-midnight-color-base50) !important;
  font: 11px var(--base-font-family) !important;
}

ul.rdr-teams li p {
  color: var(--ff-midnight-color-base30);
}

/* Rider box */

.rdr-info {
  /* width: 50%; */
  width: calc(55% - 40px);
  background: var(--ff-midnight-color-base110);
  border-radius: 12px;
  padding: 10px;
  margin-right: 20px;
  margin-bottom: 10px;
}

.rdr-info h3 {
  display: none;
}

.palmares {
  /* width: 50%; */
  width: 45%;
}

.pps li span:nth-child(3) {
  color: var(--ff-midnight-color-base30);
}

.pps li span {
  font-family: var(--base-font-family);
}

a.situ-live {
  margin-bottom: 10px;
}


/*  results - sort nav ------------------------------------------------------------------------------------------- */


.pps li span:nth-child(1) {
  background: var(--ff-midnight-color-base90);
}

.sortByResults {
  color: var(--ff-midnight-color-base30);
  float: right;
}

.sortByResults a.cur:link, .sortByResults a.cur:visited {
  color: var(--ff-midnight-color-base30);
  border: 1px solid var(--ff-midnight-color-base70);
}

.sortByResults a:link, .sortByResults a:visited {
  color: var(--ff-midnight-color-base50);
  padding: 2px 5px;
  margin-left: 2px;
}

/*  RIDER STATISTICS ------------------------------------------------------------------------------------------- */


.barchart_simple .bgCont {
  background: var(--ff-midnight-color-base95);
}

.runningPntScore {
  border: 1px solid var(--ff-midnight-color-base90);
}

.runningPntScore .line {
  border-top: 1px dashed var(--ff-midnight-color-base90);
}

.runningPntScore .bgCont {
  border-right: 1px solid var(--ff-midnight-color-base90);
}

.runningPntScore .tag {
  background: var(--ff-midnight-color-base);
}

.detailsBar div {
  background: var(--ff-midnight-color-base90) !important;
}

div[style*="border-bottom: 1px solid #ddd"] {
  border-bottom: 1px solid var(--ff-midnight-color-base90) !important;
}

.statDivRight div[style*="border-top: 1px solid #ccc"] {
  border-top: 1px solid var(--ff-midnight-color-base90) !important;
}

/*  HEAD TO HEAD PAGE ------------------------------------------------------------------------------------------- */

.hthImg {
  border: 1px solid var(--ff-midnight-color-base90);
}

.hthImg img {
  display: block;
}

.kpiBG {
  background: var(--ff-midnight-color-base);
}

.hthCompare td {
  border-top: 1px solid var(--ff-midnight-color-base95);
}

.hthCompare2 td {
  border-top: 1px solid var(--ff-midnight-color-base95);
}

.hthResults td {
  border-top: 1px solid var(--ff-midnight-color-base95);
}

.runningPntScore .bgCont:hover {
    background: var(--ff-midnight-color-base95);
}

/* Other stuff */

.hpFilter:link, .hpFilter:visited {
  border: 1px solid var(--ff-midnight-color-base90);
  color: var(--ff-midnight-color-base30);
}

.basic tr.sum td {
  border-top: 3px double var(--ff-midnight-color-base90);
  background: var(--ff-midnight-color-base110);
}

.barchart_simple .barCont:hover .bgCont {
    background: var(--ff-midnight-color-base);
}



/*  LIVE SITUATION PAGE ------------------------------------------------------------------------------------------- */


.situ2 li:first-child div.line {
  background: var(--ff-midnight-color-base30);
}

.timeline li div.eventCont {
  border-bottom: 1px solid var(--ff-midnight-color-base90);
}

.timeline li div.tl div.bol,
.situ2 li div.bol2 {
  border: 1px solid var(--ff-midnight-color-base70);
  color: var(--ff-midnight-color-base10);
  background: var(--ff-midnight-color-base120);
  font-weight:400;
}

.timeline li div.tl div.line {
  border-left: 2px solid var(--ff-midnight-color-base50);
}

/* fix inline css border */
#situCont + div + .w56 > div:nth-child(2) {
  border: 0px solid #ddd !important; 
}

.timeline li table.compact tr td {
  border: 1px solid var(--ff-midnight-color-base90);
  border-right: 0;
  border-left: 0;
}

.keypoints th {
