css:
@-moz-document domain("www.gg.pl") {
:root {
    --bg: #121212;
    --bg2: #212121;
    --bg3: #323232;
    --border: 1px solid #212121;
    --bordercolor: #212121;
    --fg: #FAFAFA;
    --fg2: #999999;
    --invert: invert(.93);
}

body {
    background-color: var(--bg);
}

/*#sr-webgg-account {
    background-color: var(--bg2);
    border: var(--border);
}*/

#ext-comp-1002 {
    background-color: var(--bg2);
}

#sr-webgg {
    border: var(--border);
}

.sr-header-template-1 .avatar-widget {
    border: var(--border);
}

#sr-contact-list-panel {
    background-color: transparent;
    border: none;
}

#sr-contact-list-search {
    background-color: transparent;
    border: none;
}

.search-input {
    background-color: transparent;
    filter: invert(.93);
    border: none;
}

.search-cancel-btn {
    filter: invert(.93);
}

#sr-contact-list-container {
    overflow-y: auto;
}

.sr-contact {
    border-top: var(--border);
    border-bottom: none;
}

.sr-contact:hover {
    background-color: var(--bg2);
    border-color: var(--bordercolor);
}

.sr-contact-name SPAN {
    background: none;
}

.sr-contact.x-item-selected, .sr-contact.x-item-selected:hover {
    background-color: var(--bg3);
    box-shadow: none;
}

.sr-user-name {
    color: var(--fg);
}

.sr-user-desc,.sr-contact.show-description .sr-contact-desc .sr-comment {
    color: var(--fg2);
}

.sr-contact .sr-contact-name {
    color: var(--fg);
}

.chat-accordion-item.expanded .opened-item {
    background-color: var(--bg);
    border: var(--border);
}

.min-button I, .close-button I {
    filter: invert(.93);
}

.header .title {
    color: var(--fg);
    text-shadow: none;
}

.add-button, .talk-button {
    background: none;
    background-color: var(--bg2);
    border-radius: 2px;
}

.add-button I {
    filter: invert(.93) grayscale();
}

.add-button:hover {
    background-color: var(--bg3);
}

.chat-accordion-item .body {
    background: var(--bg);
    border: var(--border);
}

.chat-accordion-item .messages-placeholder.d-none + .body {
    border-top: var(--border);
}

.chat-area {
    border: var(--border);
}

.chat-submit-toolbar {
    background-color: transparent;
}

.sr-message-area #chat-text {
    background-color: var(--bg);
    color: var(--fg);
}

.ml__item-part-content {
    background-color: var(--bg2);
    color: var(--fg);
    border-radius: 16px;
    padding: 8px 12px;
    width: auto !important;
}

.ml__item--incoming .ml__item-part-content {
    background-color: var(--bg3);
}

.ml__item {
    margin: 10px;
}

.ml__item--continued {
    margin-top: -7px;
}

.ml__item--incoming .ml__item-username, .ml__item-time {
    color: var(--fg2);
}

#sr-footer UL.first LI A, #sr-footer {
    color: var(--fg2);
}

#sr-footer UL LI A:hover {
    color: var(--fg);
}

.closed-item {
    border: var(--border);
    background-color: var(--bg);
}

.closed-item:hover {
    background-color: var(--bg2) !important;
    border: var(--border) !important;
}

.closed-item .fadeout {
    display: none;
}

.closed-item .nick {
    color: var(--fg);
}

.closed-item .chat-btns .close-btn I {
    filter: var(--invert);
}

.opened-item .chat-toolbar A I {
    filter: var(--invert) grayscale(); 
}

.opened-item .chat-toolbar A {
    text-shadow: none;
}

.opened-item .chat-toolbar A:hover {
    border: 1px solid transparent;
    border-radius: 0;
    box-shadow: none;
}

.opened-item .chat-toolbar A.giphy-btn.active, .opened-item .chat-toolbar A.giphy-btn:hover, .chat-submit-toolbar .chat-submit-buttons .send-btn:active, .chat-submit-toolbar .chat-submit-buttons .send-bt {
    color: var(--fg);
}

.chat-submit-toolbar .chat-submit-buttons .send-btn:hover {
    color: var(--fg);
    border-left: 1px solid transparent;
}

.opened-item .chat-toolbar A.giphy-btn.active, .opened-item .chat-toolbar A.giphy-btn {
    color: var(--fg2);
}

.chat-submit-toolbar .chat-submit-buttons {
    color: var(--fg2);
    background-image: none !important;
    background-color: var(--bg) !important;
    box-shadow: none;
    border: var(--border);
    border-top: 0;
}

.chat-submit-toolbar .chat-submit-buttons .enter-btn:active I, .chat-submit-toolbar .chat-submit-buttons .enter-btn.active I {
    filter: var(--invert) hue-rotate(180deg);
}

.chat-submit-toolbar .chat-submit-buttons .enter-btn:active, .chat-submit-toolbar .chat-submit-buttons .enter-btn.active {
    box-shadow: none;
    background: none;
}

.chat-submit-toolbar .chat-submit-buttons .enter-btn {
    border-right: var(--border);
}

.chat-submit-toolbar .chat-submit-buttons .enter-btn:hover  {
    background-color: var(--bg2);
}

.chat-submit-toolbar .chat-submit-buttons .enter-btn I {
    filter: var(--invert);
}

.chat-accordion-item .body.normal .conversation-aol {
    background-color: var(--bg);
    border-bottom: var(--border);
}

.share-links-widget .share-links-header, .share-links-widget .share-links-footer {
    border: var(--border);
}

.share-links-widget .share-links-resultlist LI {
    border: var(--border);
    border-top: 0;
}

.html5-uploader-container a, .html-uploader-container a {
    background: none !important;
}

.html5-uploader-container a:hover, .html-uploader-container a:hover {
    filter: brightness(20);
}

body {
    max-height: 100vh !important;
    overflow-y: auto;
}
}
