/**********************************/

/* The Club total ani v1.0 skin  */

/* Date: 11/04/2009               */

/* Made by: Maek              */

/**********************************/



@-moz-document domain("hattrick.org") {



.boxLeft

{ 

	background: black !important; 

}







#shortcuts a

{

        float: left !important; 

        margin-right: 2px !important;

        margin-bottom: 3px !important; 

}



#mainBody

{

	

	background: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/Rain-01-june.gif)  !important; 

	color: #19D1F0 !important; 

	border: none !important;



}



#top

{

	height: 0px !important;

	position: relative !important;

	z-index: 50 !important;

        background: transparent  !important;

}

#footer a

{

        color: Green !important;

        font-weight: bold !important;

}

#ticker

{

	position: absolute !important;

	top: 20px !important;

	left: -280px !important;

font-weight: bold !important;

text-decoration: blink   !important;





}









#ticker div a

{

	padding-bottom: 2px !important;

	color: yellow !important;

	text-decoration: none !important;

	line-height: 1.1em !important;

}

#shortcuts

{

	margin-top: 25px !important;

	margin-left:0px !important;

}

#teamLinks

{

        color: DarkGoldenrod !important;

        margin-top: 15px !important;

	margin-left: 7px !important;

}

div#teamLinks a

{

        color: #19D1F0 !important;

}

/*--Users online - week - date - time ----------------------------*/

div[id="online"] {color: red !important;

position: absolute !important;

	top: 40px !important;

	left: 370px !important;

font-weight: bold !important;

}



div[id="time"] {color: red !important;

position: absolute !important;

	top: 20px !important;

	left: 370px !important;

font-weight: bold !important;

 }



/*--Page background-----------------------------------------------*/

body {background: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/Rain-01-june.gif) !important;}



/*----centre title-------("Offizielle Hattrick-Mitteilungen")--------------*/

h1 

{

        color: #19D1F0 !important;

        border-bottom: none !important;

border: none !important;

}

/*-----all boxHead--------------------*/

h2 

{

        color: #fae822 !important;  

        background: black !important;

border: none !important;

}

/*-----centre news--------------------*/

.boxBody h2 

{ 

        color: #19D1F0 !important; 

        background: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/Rain-01-june.gif) !important;

        border-bottom: solid 1px #b4b4b4 !important;

border: none !important;

}

/*-----------boxHeads----------------------------------------------------------------------------------------*/

/*Left menu*/

.subMenuBox .boxHead

{

        color: #fae822 !important;  

	background: black !important;







}

.subMenuBox .boxHead .boxLeft

{

        color: #fae822 !important; 

        background: black  !important;





}

/*Centre box*/

.main .boxHead h2 

{

        color: #fae822 !important; 

	background: black !important;







}

/*Right menu*/

.sidebarBox h2 

{

        color: #fae822 !important; 

	background: black  !important;





}



.boxBody

{

	background: black !important; 

	color: orange !important; 

	border: none !important;



}





/*----- Press, GB, Team events ------------------------------------------------------------------------------*/

.main .mainBox h2 

{

	border-bottom: solid 2px #19D1F0  !important;

	color: #19D1F0 !important;

	font-size: 14px !important;

	font-weight: bold !important;

	padding: 1px !important;





}

/*Central header paths*/

h2 a 

{

        color: #fae822 !important;

 text-decoration: none !important;

}

h2 a:hover 

{

        color: #FF0000 !important;  

}

/*News and team events date*/

.date

{

	color: #808080 !important;

	padding-right: 8px;

	font-weight: 400;

}

/*Left Menu titles*/

h3 

{

        color: #19D1F0!important;

        border-bottom: solid thin #19D1F0!important;



}

/*General Links*/

a

{

        color: orangered !important;

	text-decoration: none !important;

}

/*----subMenu and sidebar links------------------------------------------------------------------------------*/

.subMenuBox a, .subMenuBox a

{

        text-decoration: none !important;

	color: #19D1F0 !important;







}





.subMenuBox:after



{ content: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/FootballEngland.gif)

 !important



   

}











.sidebarBox a 

{

        text-decoration: underline !important;

	color: white !important;



}





.sidebarBox:after



{ content: none

 !important





}













/*Bot teams link*/

div a[class="shy"] 

{

        color: BlueViolet !important;

}

/*Hide Hattrick logo*/

#logo

{

        background: none !important;

}

/*Hide ticker's stars*/

#ticker div

{

        /*background: none !important;*/

}

/* central page background */

#content

{

        background-image: none !important;

        background: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/Rain-01-june.gif) !important;   

}

/*Image header (banner)*/

#header 

{

        background: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/englandbadge.gif) no-repeat !important;

	height: 75px !important;

	padding-top: 78px !important;

        left: 250px !important;

}

/*--------Top menu settings----------------------------------------------------------------------------------*/

#menu

{

	height: 32px !important;

	margin-top: 0px !important;

        background: transparent !important;

        text-decoration: none !important;



left: -300px !important;

}

#menu a

{

	line-height: 25px !important; 

        font-size: 14px !important; 

        color: #FF0000 !important;

text-decoration: none !important;

}

div#menu a:hover 

{

        color: #FF0000 !important; 

        background: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/Rain-01-june.gif) !important; 

}

a:hover

{

	color: #ad1b3f !important; 

text-decoration:none !important; 

}

/*bottom black line*/

div[id="bottom"]

{

        background: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/Rain-01-june.gif) !important;

}



/*---Aufstellung---------------------------------------------------------*/

div[class="name"] a       {color: #FF0000!important;}

div[class="name"]  

a:hover {color: #ad1b3f !important;}



/*---Forum---------------------------------------------------------------*/

div[class="boxBody"]   {background: url(http://i37.photobucket.com/albums/e53/sweetlimelite/hattrick_seite/Rain-01-june.gif)

 !important;}

div[class="message"] a {color: #de4747 !important; }

div[class="signature"] a {color: #de4747 !important;}



#cfPollHeader

{

	background: NONE !important;

	padding: 5px 10px;

	font-weight: bold;

        color: #FAE822 







}

.cfWrapper

{

	border: solid 1px green !important;

	margin: 0 0 12px 0;

}

.cfWrapper a

{

        color: #fae822 !important; 



}

.cfUserInfo a

{

        color: #ad1b3f !important;



}

.folderHeader a:link 

{

	color: #de4747 !important;

	font-weight: bold !important; 



}

.cfFooter a

{ 

        color: #b4b4b4 !important;

}

.folderItem div.ignore

{

        cursor: pointer;

        width: 10px;

        height: 10px;

}

.folderItem div.unignore

{

        cursor: pointer;

        width: 10px;

        height: 10px;

}

.folderItem div.unignore:hover

{



}

/*Ads section*/

div[class="insertHeader"]

{



}

div[class="insertFooter"]

{

        background:  #FAE822 

!important; 

}





