css:
@-moz-document url-prefix(https://mantis.), url-prefix(http://mantis.)
{
	/* DARK */

	.skin-3 .page-content {
		background-color: #0000008c;
	}
	.widget-toolbox {
		background-color: #a9a9a9;
	}
	.table-striped>tbody>tr:nth-of-type(odd) {
		background-color: #a7a7a7;
	}
	table {
		background-color: #c7c7c7 !important;
	}
	[class*=widget-color-]>.widget-body .table thead:first-child tr {
		background-image: -webkit-linear-gradient(top,#dad7d7 0,#949494 100%);
		background-image: -o-linear-gradient(top,#dad7d7 0,#949494 100%);
		background-image: linear-gradient(to bottom,#dad7d7 0,#949494 100%);
	}
	.skin-3 .main-container:before {
		background: none repeat scroll 0 0 #969696 !important;
	}

	/* RED */

	.widget-color-blue2>.widget-header {
		background: #c15050;
		border-color: #c15050;
	}

	a {
		color: #b73333;
	}

	.btn-primary, .btn-primary.focus, .btn-primary:focus {
		background-color: #ca4242!important;
		border-color: #ca4242;
	}

	.breadcrumb>li>a {
		color: #bd4c4c;
	}

	.skin-3 .nav-list>li.active.highlight>a, .skin-3 .nav-list>li.active>a {
		color: #cb4d4d!important;
	}

	.blue {
		color: #ca4747!important;
	}

	.btn-primary.focus:hover, .btn-primary:active:hover, .btn-primary:focus:active, .btn-primary:focus:hover, .btn-primary:hover, .open>.btn-primary.dropdown-toggle, .open>.btn-primary.dropdown-toggle.focus, .open>.btn-primary.dropdown-toggle:active, .open>.btn-primary.dropdown-toggle:focus, .open>.btn-primary.dropdown-toggle:hover {
		background-color: #aa1b1b!important;
		border-color: #ca4242;
	}

	.skin-3 .breadcrumbs {
		background-color: #d2d2d2;
	 }
	 
	 .nav-search .nav-search-input {
		border: 1px solid #e06f6f;
	}

	.nav-search .nav-search-icon {
		color: #e06f6f!important;
	}

	.widget-color-blue2 {
		border-color: #c15050;
	}

	a:focus, a:hover {
		color: #7c2323;
	}

	.btn-white.btn-primary {
		border-color: #ce8a8a;
		color: #a66666!important;
	}

	.btn-white.btn-primary.active, .btn-white.btn-primary:active, .btn-white.btn-primary:focus, .btn-white.btn-primary:hover, .open>.btn-white.btn-primary.active.dropdown-toggle, .open>.btn-white.btn-primary.dropdown-toggle {
		background-color: #f8eaea!important;
		border-color: #ce8a8a;
		color: #9f5353!important;
	}

	input[type=checkbox].ace:checked+.lbl::before, input[type=radio].ace:checked+.lbl::before {
		color: #ce3232;
	}
}
