css:
@-moz-document domain("client.poorchat.net") {
body {
    background-color: #121212;
}

.events {
    padding-bottom: 0px;
}
.events .event {
    padding-top: 0.48rem;
    padding-right: 1.6rem;
    padding-bottom: 0.48rem;
    padding-left: 0.7rem;
}

.embed .embed-inner {
    background: #0c0c0c !important;
}
.embed .embed-color {
    background-color: #333 !important;
}

.channel-input {
    background-color: #0c0c0c !important;
    padding: 2px 2px 2px 2px !important;
}
.channel-input .xddd {
    height: 0;
}
textarea{
    background-color: #040404;
    margin-bottom: 2px;
    padding-top: 0.2rem;
    padding-right: 82px;
    padding-bottom: 0.2rem;
    padding-left: 1rem;
}
.channel-input-autocomplete {
    background-color: #0c0c0c !important;
    height: 2.0rem !important;
}
.channel-input-autocomplete > div.active {
    background-color: #222 !important;
}
.channel-input-autocomplete > div.user {
    padding: 0.6rem !important;
}

.xddd > .right {
    /*Send button*/
    position: absolute;
    right: 6px;
    bottom: 3px;
}
.right .button {
    font-size: 0;
    padding: 0px;
    background-color: transparent;
}
.right .button svg {
    font-size: 16px;
}
.xddd > :not(.right) {
    /*Users button*/
    position: absolute !important;
    bottom: 5px !important;
    right: 28px !important;
}
.xdd > div {
    /*Emoticons button*/
    right: 56px !important;
    bottom: -3px !important;
}
.icon-button > svg {
    font-size: 16px !important;
}

input {
    background-color: #040404;
}

.channels .channels-header {
    background-color: #0c0c0c;
}
.channels .channels-header > div {
    padding: 2px 6px 2px 6px;
    height: 32px;
}
.channels .channels-header .button{
    background-color: #121212;
}
.buffers .buffers-content .vb .vb-content > div {
    background-color: #040404;
}
.list .button.active {
    background-color: #222;
}
.list .button:hover {
    background-color: #212121;
}
.list {
    border-bottom-color: transparent;
}


.settings {
    background-color: #121212;
}
.settings .settings-content .option .option-input input {
    background-color: #040404;
}

.panel, .panel .panel-header, .panel .panel-search {
    background-color: #121212;
}
.panel .panel-header {
    padding: 0.8rem 0.5rem 0.5rem 1.5rem;
}
.panel-content {
    padding: 0.5rem 0.5rem 0.5rem 0.5rem !important;
}

.panel-user .panel-content .button {
    background-color: #040404 !important;
}
}
