css:
@-moz-document domain("mb.srb2.org") {
/*=SRB2MB DarkRed Style=*/
/*========by Haz========*/

/*======Background stuff*/
/*Main background=======*/
body {
    background: #2d0000;
    color: #000000;
    font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    margin: 5px 10px 10px 10px;
    padding: 0px;
}
/*Sub(?)-background=====*/
.page {
    background: #2d0000;
    color: #000000;
}

/*==========Main section*/
/*Border================*/
.tborder {
    background: #280000;
    color: #000000;
    border: 1px solid #150000;
}
/*Darker Inner BG======*/
.alt1,
.alt1Active {
    background: #520000;
    color: #ffffff;
}
/*Lighter Inner BG=====*/
.alt2,
.alt2Active {
    background: #880707;
    color: #ffffff;
}
/*Clocks===============*/
.time {
    color: #dcbcbc;
}
/*Unclicked Links======*/
a:link,
body_alink {
    color: #cab610;
}
/*Clicked Links========*/
a:visited,
body_avisited {
    color: #82750c;
}
/*Top Menu=============*/
.vbmenu_control {
    background: #710000;
    color: #FFFFFF;
    font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    padding: 3px 6px 3px 6px;
    white-space: nowrap;
}
/*Board Titles=========*/
.tcat {
    background: #620b0b url(https://i.postimg.cc/QN9kNcpK/gradient-tcat.gif) repeat-x top left;
    color: #FFFFFF;
    font: bold 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
/*Column Headers=======*/
.thead {
    background: #620b0b url(https://i.postimg.cc/nr4XjVFg/gradient-thead.gif) repeat-x top left;
    color: #FFFFFF;
    font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
/*Forum Footer=========*/
.tfoot {
    background: #880707;
    color: #ffffff;
}
/*General Footer=======*/
.smallfont {
    font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    color: #FFFFFF;
}
/*Legend Text==========*/
legend {
    color: #ffb0b0;
    font: 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}

/*======Advanced Search*/
/*Panel Back===========*/
.panelsurround {
    background: #520000;
    color: #ffffff;
}
/*Panel Front==========*/
.panel {
    background: #880707;
    color: #fff;
    padding: 10px;
    border: 2px;
}
/*Search Bar Back======*/
select {
    -webkit-writing-mode: horizontal-tb !important;
    color: #fff;
    background-color: #420000;
}
/*List Item Highlights=*/
.fjdpth0 {
    background: #880707;
    color: #fff;
}
/*Selection============*/
select {
    -webkit-writing-mode: horizontal-tb !important;
    color: #fff;
    background-color: #420000;
}
/*Internal Borders=====*/
fieldset {
    display: block;
    margin-inline-start: 2px;
    margin-inline-end: 2px;
    padding-block-start: 0.35em;
    padding-inline-start: 0.75em;
    padding-inline-end: 0.75em;
    padding-block-end: 0.625em;
    border-width: 2px;
    border-style: solid;
    border-color: #520000;
    border-image: initial;
}

/*===========Menu Items*/
/*Popup Background=====*/
.vbmenu_popup {
    background: #FFFFFF;
    color: #fff;
    border: 1px solid #8c0b0b;
}
/*Option Background====*/
.vbmenu_option {
    background: #880707;
    color: #fff;
    font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    white-space: nowrap;
    cursor: pointer;
}
/*Unclicked Popup Link=*/
.vbmenu_option a:link,
.vbmenu_option_alink {
    color: #cab610;
    text-decoration: none;
}
/*Clicked Popup Link===*/
.vbmenu_option a:visited,
.vbmenu_option_avisited {
    color: #82750c;
    text-decoration: none;
}

/*Note:
Unfortunately I can't make the master server listings look any better (at least, that I know of)
So you're gonna have to suffer with green backgrounds. Sorry.*/
}
