css:
@-moz-document url-prefix("chrome-extension://clngdbkpkpeebahjckkjfobafhncgmne/edit.html") {
:root {
	--text-editable-color: #5ecbff;
	--ui-edit-color: #ffa200;
	--ui-headers-color: #4a4a4a;
	--button-main: #0bb0ff;
	--button-second: #ff3e3e;
	--main-font: 14px 'Source sans pro', arial, sans-serif;
	--main-font-size: 1.1rem;
	--pencil-svg: url('data:image/svg+xml;base64,')
}
/*****************************************/
body {
	margin: 0;
	font: var(--main-font);
	font-size: var(--main-font-size);
}
.svg-icon:hover,
.svg-icon.info,
.svg-icon.settings {
	fill: var(--ui-edit-color);
}
input[type="checkbox"]:not(.slider) {
	-webkit-appearance: none;
	-moz-appearance: none;
	position: absolute;
	left: 0;
	top: 0;
	height: 15px;
	width: 15px;
	display: inline-flex;
	border-radius: 2px;
	background-color: #9494941a;
	margin-left: 5px;
	border: none;
	transition: background-color .1s, border-color .1s;
}
.options-column input[type="checkbox"]:not(.slider),
.beautify-options input[type="checkbox"]:not(.slider) {
	top: 3px;
}
input[type="checkbox"]:hover {
	border: none;
}
input[type="checkbox"]:checked,
input[type="checkbox"]:checked:hover {
	background-color: var(--text-editable-color);
}
input[type="checkbox"]:not(:checked):hover {
	background-color: #9494941a;
}
svg.svg-icon.checked {
	display: none !important;
}
label {
	padding-left: 20px;
}
.options-column label,
.beautify-options label {
	padding-left: 27px;
}
button {
	cursor: pointer;
	border: none;
	background-image: none;
	background-color: var(--button-main);
	color: white;
	padding: 5px 11px;
	font-size: 1rem;
	border-radius: 3px;
}
button:hover {
	background-color: #4bc5ff !important;
}
button[id^="cancel"] {
	background-color: var(--button-second)
}
button:disabled,
select:disabled,
option:disabled,
select[disabled] > option {
	color: white;
	opacity: 0.4;
	cursor: not-allowed;
}
#header {
	width: 380px;
	padding: 2rem;
	border-right: none;
}
#header :-webkit-any(h1,
h2) {
	color: var(--ui-headers-color);
}
input#name,
input.applies-value.style-contributor {
	font-size: 1.1rem;
	padding: 20px 0;
	width: 280px;
	border: none;
	text-align: center;
	background-color: #0bb0fe0d;
	font-weight: 400;
	color: #000000;
}
#options [type="number"] {
	border: none;
	color: var(--text-editable-color);
}
select {
	border: none !important;
	color: #0bb0fe;
}
.option.aligned label {
	text-align: right;
	padding-right: 15px !important;
}
#basic-info-enabled {
	margin-top: 10px;
}
#sections {
	padding-left: 400px;
}
.section .CodeMirror {
	margin-bottom: .875rem;
	margin-top: 10px;
	font-family: Consolas, monospace;
	font-size: 1rem;
	outline: none;
	border: solid #f2f2f2 1px;
	min-height: 450px;
	height: 460px;
}
.CodeMirror::selection {
	background-color: var(--button-second);
}
.section label.code-label {
	padding: 0;
	font-size: 1.3rem;
}
div#help-popup {
	top: 100px;
	padding: 0;
	border-radius: 5px;
}
svg#sections-help {
	opacity: 0.2;
}
#help-popup .contents {
	padding: 1.3rem;
}
#help-popup .title {
	padding: 25px 0 15px 25px;
	margin: 0;
}
#help-popup .dismiss {
	right: 15px;
	top: 18px;
	width: 20px;
	height: 20px;
}
#help-popup .buttons {
	margin-top: 15px;
}
#help-popup .buttons {
	margin-top: 15px;
}
input.applies-value.style-contributor {
	font-family: Consolas, monospace;
	margin-left: 30px;
	margin-right: 30px;
}
.select-resizer {
	text-align: right !important;
}
.remove-applies-to .svg-icon {
	fill: var(--button-second);
}
.add-applies-to .svg-icon {
	fill: var(--button-main);
}
.applies-to label {
	padding-top: 9px;
}
.add-applies-to,
.remove-applies-to {
	margin-top: 10px;
}
input#name,
input.applies-value.style-contributor {
	background-image: var(--pencil-svg);
	background-repeat: no-repeat;
	background-size: 6%;
	background-position: bottom 10px right 13px;
}
input.applies-value.style-contributor {
	background-size: 2%;
}
.option.aligned .select-resizer,
li.applies-to-item .select-resizer {
	width: 180px;
}
.svg-icon.select-arrow {
	fill: #0bb0fe;
}
.CodeMirror-linenumber {
	color: #0000002b;
}
.CodeMirror-selected {
	background: #00aeff61 !important;
}
}
