css:
@-moz-document url-prefix("http://youtube.com/"), url-prefix("https://youtube.com/")
{



:root {
    --nav:#001c39;
    --nav-sub: #01264d;
    --header: #080809;
    --nav-text: #395062;
    --nav-text_drk: #535b62;
    --main-bg: #151515;
    --white: #f6f6f7;
    --white_semi_transaparent: #f6f6f7b0;
    --yt_red_transparent: #df01013b;
    --yt_red_semi_transparent: #df01018a;
    --yt_red: #440101;
    --yt_red_brt: #8a0000;
    --green: #089944;
    --color001: #8f9190;
    --color002: #ff7300;
    --black: #000;
    --black_semi_transparent:#00000069;
    --black_semi_semi_transparent:#000000d1;
    --black_very_transparent:#0000000a;
    --black_semi_mid_transparent:#0000001a;
}


::-webkit-scrollbar { width: 0 !important }




ytd-active-account-header-renderer, #sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer:not(:last-child), #sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer{
  background: var(--main-bg)!important;  
}

ytd-active-account-header-renderer{
    border-left:2px solid var(--yt_red);
    border-right:2px solid var(--yt_red);
    border-top:2px solid var(--yt_red);
}

#sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer:not(:last-child){
    border-left:2px solid var(--yt_red);
    border-right:2px solid var(--yt_red);
}


#sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer{
    border-left:2px solid var(--yt_red);
    border-right:2px solid var(--yt_red);
    border-bottom:2px solid var(--yt_red);
    padding-bottom:2px;
}


ytd-author-comment-badge-renderer, #name.ytd-author-comment-badge-renderer{
    background: var(--yt_red) !important;
    color: var(--nav-text_drk)!important;
}



/* elements of main body color */

ytd-page-manager > *.ytd-page-manager, ytd-mini-guide-entry-renderer {
    background: var(--main-bg)!important;
    color: var(--white)!important;
}

#container.ytd-searchbox{
    background: var(--main-bg)!important;
    border: none;
    border-radius: 5px 5px 5px 5px;
    box-shadow: 0px 0px 5px 3px var(--nav-text);
    
}


#search-icon-legacy.ytd-searchbox{
    background: var(--yt_red_transparent)!important;
    border: none;
    box-shadow: 0px 0px 5px 3px var(--header);
    margin-left:3%;
    border-radius: 3px 3px 3px 3px;
}


#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox{
    color: var(--header)!important;
}


#container.ytd-masthead, ytd-guide-renderer{
    background: var(--main-bg)!important;
    color: var(--white)!important;
}


.toggle-button.paper-toggle-button{
    background: var(--yt_red)!important;
}



paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button{
    
    background: var(--yt_red_brt)!important;
}





paper-item.ytd-guide-entry-renderer{
    background: var(--yt_red_transparent)!important;
    border-bottom: 1px solid;
    margin-bottom: 10%;
    margin-left: 3%;
    width: 65%;
    box-shadow: 0px 0px 3px 3px #000000;
}


#author-thumbnail.ytd-comment-renderer yt-img-shadow.ytd-comment-renderer,#author-thumbnail.ytd-comment-simplebox-renderer,#avatar.ytd-video-owner-renderer{
    box-shadow: 3px 3px 13px 5px var(--black);
    border: 3px inset var(--nav-text) !important;
    transform: rotate(325deg);
}


paper-button.ytd-subscribe-button-renderer{
    background: var(--yt_red)!important;
    box-shadow: 5px 5px 3px 5px var(--black);
}



ytd-compact-video-renderer #video-title.ytd-compact-video-renderer, .title.ytd-video-primary-info-renderer, yt-formatted-string[has-link-only_]:not([force-default-style]) a.yt-simple-endpoint.yt-formatted-string, .content.ytd-video-secondary-info-renderer, yt-formatted-string.ytd-comments-header-renderer, h3.ytd-compact-playlist-renderer, #upnext.ytd-compact-autoplay-renderer, #title.ytd-rich-list-header-renderer, #video-title.ytd-video-renderer, ytd-grid-radio-renderer #video-title.ytd-grid-radio-renderer, #label.ytd-compact-link-renderer, #subtitle.ytd-compact-link-renderer, #account-name.ytd-active-account-header-renderer{
    color: var(--nav-text) !important;
}


#author-text.yt-simple-endpoint.ytd-comment-renderer, #video-title.ytd-playlist-renderer, #email.ytd-active-account-header-renderer, #label.ytd-toggle-theme-compact-link-renderer{
    color: var(--yt_red_brt) !important;
}


.center-aligned.ytd-horizontal-card-list-renderer{
    background: none;
    border-radius:0;
    content:url("http://icons.iconarchive.com/icons/designbolts/hand-stitched/48/YouTube-icon.png")!important;
}


.arrow.yt-horizontal-list-renderer {
    background: none;
    border-radius:0;
    content:url("http://icons.iconarchive.com/icons/designbolts/hand-stitched/48/YouTube-icon.png")!important;
}



#promo-info.ytd-primetime-promo-renderer, #contents.ytd-primetime-promo-renderer, #spacer.ytd-primetime-promo-renderer,yt-icon-button.ytd-button-renderer{
    visibility: hidden;
}







ytd-playlist-thumbnail #thumbnail.ytd-playlist-thumbnail{
    border-radius: 15px 15px 15px 15px;
}


ytd-thumbnail{
    margin-top: 20px;
    margin-bottom:10px;
    
}




#content-text.ytd-comment-renderer{
    color: var(--nav-text_drk) !important;
    
}




ytd-mini-guide-renderer.ytd-app{
   background: var(--main-bg)!important;
}
a.ytd-mini-guide-entry-renderer{
    background: var(--yt_red)!important;
    border-bottom: 1px solid;
    margin-bottom: 10%;
    margin-left: 3%;
    box-shadow: 0px 0px 3px 3px #000000;
}

.title.ytd-mini-guide-entry-renderer{
    color: var(--white)!important;
}

a.ytd-mini-guide-entry-renderer:hover{
    background: var(--yt_red_semi_transparent)!important;
}



paper-item.ytd-guide-entry-renderer:hover{
    background: var(--yt_red_semi_transparent)!important;
}


#logo-icon-container.ytd-topbar-logo-renderer{
    content: url(http://icons.iconarchive.com/icons/graphics-vibe/cloud-social/128/youtube-icon.png);
    Max-width: 65% !important;
    height: 50% !important; 
     border-radius: 100%;
    box-shadow: 0px 0px 3px 3px #710000;
}


/* video player */
.html5-video-player .video-click-tracking, .html5-video-player .video-stream{
    border-radius: 5px 5px 5px 5px;
}



#title.ytd-shelf-renderer{
    color: var(--yt_red_brt)!important;
}

ytd-grid-video-renderer #video-title.yt-simple-endpoint.ytd-grid-video-renderer{
    color: var(--nav-text)!important;
}


ytd-compact-autoplay-renderer{
    margin: 0;
    padding:0;
}



 #thumbnail.ytd-thumbnail,ytd-playlist-thumbnail{
    border-radius: 15px 15px 15px 15px; 
    width: 100%;
    box-shadow: 0px 0px 5px 3px var(--yt_red_brt);
    transform: rotate(-15deg) scale(0.9);
    height:90%;    
}

ytd-search-refinement-card-renderer{
    transform: rotate(-15deg) scale(0.8) translate(10px, 2px);
     box-shadow: 0px 0px 5px 3px var(--yt_red_brt);
    border-radius: 15px 15px 15px 15px; 
    width:154px;
    margin-top:5px;
    margin-bottom:5px;
    
}


#card-title.ytd-search-refinement-card-renderer div.ytd-search-refinement-card-renderer{
    color: var(--nav-text)!important;
    font-size: 15px;
    
}



a.ytd-search-refinement-card-renderer{
    background: none;
    
}



#items.yt-horizontal-list-renderer > ytd-post-renderer.yt-horizontal-list-renderer{
    background: var(--main-bg)!important;
    color: var(--white)!important;
    height:90%;
    width:95%;
    margin-left: 3%;
    margin-top: 3%;
    box-shadow: 0px 0px 5px 3px var(--nav-text);
    border-radius: 15px 15px 15px 15px;
}

ytd-post-renderer[uses-compact-lockup_] #home-content-text.ytd-post-renderer{
    color: var(--nav-text)!important;
}

ytd-button-renderer yt-formatted-string.ytd-button-renderer{
     color: var(--nav-text)!important;
}



.sbsb_c, .sbsb_b, .sbsb_d, .sbdd_c, .sbsb_a,.sbdd_b, .sbfl_b

{
    background: #0000 !important;
    color: var(--white)!important;
    border: none;
    border-radius: 5px 5px 5px 5px;
}


.sbsb_c:hover{
    background: var(--yt_red_semi_transparent)!important;
    box-shadow: 0px 0px 3px 3px #000000;
    margin-bottom: 5px;
}

.sbsb_c{
    background: #040404 !important;
    margin-bottom: .3%;
    padding-bottom:1.5%;
    padding-top:1.5%;
    height: 15px;
    box-shadow: 0px 0px 3px 3px var(--yt_red_semi_transparent)!important;
}

.sbpqs_d{
    text-align:center;
}

.sbsb_a{
   padding: 3px; 
}

.sbpqs_a:hover, .sbpqs_a{
     color: var(--nav-text)!important;
    
}

.sbqs_c{
    text-align: center;
}



#progress.ytd-thumbnail-overlay-resume-playback-renderer{
    background: var(--yt_red)!important;
    
}

ytd-thumbnail-overlay-resume-playback-renderer{
    transform: translate(20px, -5px);
    width: 75%;
    background: var(--black_semi_transparent)!important;
}


ytd-thumbnail-overlay-time-status-renderer{
    background: var(--black_semi_semi_transparent) !important;
    border-radius: 3.5px 3.5px 3.5px 3.5px;
    border: 0.5px solid  var(--black_semi_transparent);
    transform: translate(0px, 0px);
    color: var(--white);
    font-weight: lighter;
    
    
}




.html5-video-player
{
    
    border-radius: 15px 15px 15px 15px;
    box-shadow: 0px 0px 10px 10px var(--black);
    border: 5px inset var(--yt_red_transparent);
}

ytd-unlimited-offer-module-renderer{
    display:none !important;
}

ytp-play-progress{
    content:none;
}


.ytp-hover-progress-light{
    background: var(--yt_red_transparent);
}


.ytp-progress-bar-container:hover:not([aria-disabled=true]) .ytp-scrubber-button{
    transform: translate(0px, -10px);
    background: #0000;
    content:url("https://media1.giphy.com/media/krvyPHazQqqmQ/giphy.webp?cid=790b7611bfad0bb9131ba8e655b3afd81562b3715b0801a7&rid=giphy.webp")!important;
    min-height: 30px;
    min-width: 30px; 
    border-radius:100%;
    opacity: 0.7;
}




.ytp-miniplayer-ui{
    transform: translate(0px, -75px);
}



.ytp-scrubber-container{
    background: none;
}


.ytp-preview.ytp-tooltip, .ytp-tooltip-bg{
    background: none;
    transform: translate(0px, -25px);
    padding: 0;
    border-radius: 10px 10px 10px 10px !important;
}

.ytp-tooltip-bg{
    box-shadow: 0px 0px 3px 5px var(--yt_red_brt);
    
}



.ytp-tooltip.ytp-preview:not(.ytp-text-detail) .ytp-tooltip-text-wrapper {
    transform: translate(50px, -85px) !important;
    background: none;
    
}


.ytp-preview.ytp-has-duration .ytp-tooltip-duration{
    transform: translate(-15%, -300%) !important;
    background: var(--black_semi_transparent)!important;
    
}


.ytp-tooltip-duration, .ytp-tooltip-text{
    background: none !important;
    border-radius: 5px 5px 5px 5px !important;
}



.ytp-progress-bar{
    background: none !important;
}


.ytp-play-progress{
    background: var(--yt_red_brt) !important;
}

.ytp-load-progress{
     background: var(--main-bg) !important;
}


.ytp-progress-bar-container:hover:not([aria-disabled=true]) .ytp-progress-list, .ytp-touch-mode .ytp-progress-bar-container:not([aria-disabled=true]) .ytp-progress-list, .ytp-drag .ytp-progress-list{
    background: var(--black_very_transparent) !important; 
}

.ytp-chrome-controls{
    opacity: 0.3;
}




  
  
}
