css:
@-moz-document domain("fhsched.com") {
/* This Section Is For The Light Theme */

body{
    background-image: url(https://cdn02.nintendo-europe.com/media/images/10_share_images/games_15/nintendo_3ds_25/H2x1_3DS_PokemonSun_enGB_image1600w.jpg);
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center;
    background-size: cover;
}
.header{
    background-color: #fff0;
    border-bottom: #fff0;
}
.header a{
    color: #000
}
.time-schedule{
    background-color: #fff0
}
.btn{
    background-position: center;
    background-repeat: no-repeat;
    background-color: #fff0;
    background-size: 100px 100px;
    background-image: url(https://www.pikpng.com/pngl/b/494-4945371_master-ball-sprite-png-png-download-pixel-art.png);
    border-radius: 0%;
}
.btn:hover{
    background-position: center;
    background-color: #fff0;
    background-repeat: no-repeat;
    background-size: 100px 100px;
    background-image: url(http://pixelartmaker.com/art/1fd7db1a2b0d6b0.png);
}
.dot:hover{
    background-image: url(http://pixelartmaker.com/art/1fd7db1a2b0d6b0.png);
    background-color: #fff0;
    background-size: cover;
}
.dot{
    background-size: cover;
    border-radius: 0%;
    background-image: url(https://www.pikpng.com/pngl/b/494-4945371_master-ball-sprite-png-png-download-pixel-art.png);
    background-color: #fff0;
}
.dot-cur{
    background-image: url(https://www.nicepng.com/png/full/267-2670644_ultra-ball-ultra-ball-pixel-art.png) !important
}
footer{
    background-color: #fff0
}
footer .red-footer{
    background-color: #fff0
}
.splash{
    background-color: #fff0
}
.lunchBtn{
    background-color: #fff0;
    background-image: url(https://www.pikpng.com/pngl/b/494-4945371_master-ball-sprite-png-png-download-pixel-art.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 150px 150px;
    color: #000
}
.lunchBtn:Hover{
    background-color: #fff0;
    background-image: url(http://pixelartmaker.com/art/1fd7db1a2b0d6b0.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 150px 150px;
    color: #000;
}
.wrongLunchBtn{
    background-color: #fff0;
    background-image: url(https://www.pikpng.com/pngl/b/494-4945371_master-ball-sprite-png-png-download-pixel-art.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 100px 100px;
    color: #000;
}
.wrongLunchBtn:Hover{
    background-color: #fff0;
    background-image: url(http://pixelartmaker.com/art/1fd7db1a2b0d6b0.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 100px 100px;
    color: #000;
}
.ham-a{
    background-color: #fff0;
}
.ham-a:hover{
    background-color: #fff0;
}
.slider{
    background-color: #fff3;
}
.slider li a{
    color: black;
    font-family: "Anton";
    border-radius:10px;
}
.slider li a:hover{
    background-color: #fff3;
    color: black;
    border-radius:10px;
}
.slider .close-sidebar:hover{
    background-color: #fff0;
    border-radius:10px;
    background-image: url(http://pixelartmaker.com/art/1fd7db1a2b0d6b0.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 60px 60px;
}
.slider .close-sidebar{
    background-image: url(https://www.pikpng.com/pngl/b/494-4945371_master-ball-sprite-png-png-download-pixel-art.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 60px 60px;
}
.national-guard-banner{
    background-color: #fff3;
}
.national-guard-banner a{
    color: #000;
}
.national-guard-banner .container{
    color: #000;
}
.national-guard-banner .container .close-sidebar:hover{
    background-color: #fff0;
    border-radius:10px;
    background-image: url(http://pixelartmaker.com/art/1fd7db1a2b0d6b0.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 60px 60px;
}
.national-guard-banner .container .close-sidebar{
    background-color: #fff0;
    border-radius:10px;
    background-image: url(https://www.pikpng.com/pngl/b/494-4945371_master-ball-sprite-png-png-download-pixel-art.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 60px 60px;
}
.ham{
    background-color: #fff0;
    border-radius:10px;
    background-image: url(https://www.pikpng.com/pngl/b/494-4945371_master-ball-sprite-png-png-download-pixel-art.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 60px 60px;
}
.ham:hover{
    background-color: #fff0;
    border-radius:10px;
    background-image: url(http://pixelartmaker.com/art/1fd7db1a2b0d6b0.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 60px 60px;
}

/* Everything Past This Point Is For The Dark Theme */

body.dark{
    background-image: url(https://cdn02.nintendo-europe.com/media/images/10_share_images/games_15/nintendo_3ds_25/H2x1_3DS_PokemonMoon_enGB_image1600w.jpg);
