    border-radius: 0%;
    color: #000;
}
body.dark .btn:hover{
    background-position: center;
    background-color: #fff0;
    background-repeat: no-repeat;
    background-size: 100px 100px;
    background-image: url(http://pixelartmaker.com/art/1fd7db1a2b0d6b0.png);
    color: #000;
}
body.dark .dot:hover{
