}
body.dark .wrongLunchBtn:Hover{
    background-color: #fff0;
    background-image: url(http://pixelartmaker.com/art/1fd7db1a2b0d6b0.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 100px 100px;
    color: #000;
}
body.dark .remaining-time{
    color: #000;
}
body.dark a:link{
    color: #000;
}
body.dark .period-countdown{
    color: #000;
}
body.dark .ham-a{
    background-color: #fff0;
}
body.dark .ham-a:hover{
    background-color: #fff0;
}
body.dark .slider{
    background-color: #fff3;
}
body.dark .slider li a{
    color: black;
    font-family: "Anton";
}
body.dark .slider li a:hover{
    background-color: #fff3;
    color: black;
}
body.dark .slider .close-sidebar:hover{
    background-color: #fff0;
}
}
