css:
@-moz-document url-prefix('https://docs.google.com/document/d') {
body {
  background-color: #222;
  color: #ddd;
}
#docs-chrome {
  background: #222;
}
.docs-titlebar-buttons {
  background-color: #222 !important;
}
.docs-icon-img {
  filter: invert(100%) !important;
}
.sketchy-icon-accessible-img::before {
  filter: invert(100%);
}
.menu-button {
  color: #ddd !important;
  background: #222 !important;
}
#docs-toolbar-wrapper {
  background: #333 !important;
  border-top: #282828 !important;
  border-bottom: #282828 !important;
}
.goog-toolbar-button, .docs-title-widget, .docs-title-input-label, .docs-title-input {
  background-color: #222 !important;
  color: #ddd !important;
}
.docs-omnibox-autocomplete, .docs-omnibox-input {
  background-color: #222 !important;
  color: #ddd !important;
  border: none !important;
}
.goog-toolbar-combo-button {
  background-color: #222 !important;
  color: #ddd !important;
  margin-top: 4px !important;
  height: 26px !important;
}
.goog-toolbar-combo-button-caption {
  vertical-align: middle !important;
}
.docs-material .goog-toolbar-combo-button-input {
  color: #ddd;
}
.goog-toolbar-menu-button {
  background-color: #222 !important;
  border-left: #282828 !important;
}
.goog-toolbar-separator {
  border-left: 1px solid #222 !important;
}
.docs-material .goog-toolbar-button, .docs-material .goog-toolbar-menu-button {
  margin: 1px 1px 0;
  height: 26px;
  line-height: 26px;
  color: #ddd !important;
}
#docs-side-toolbar {
  margin-top: 4px !important;
}
#workspace-container {
  background-color: #282828 !important;
}
.canvas {
  box-shadow: 0 0 2px #000 !important;
  border: 1px solid #222;
}
.docs-explore-widget {
  display: none;
}
.docs-horizontal-ruler {
  background: #222;
}
.docs-ruler-background-inner {
  background: #2c2c2c;
  border: 1px solid #333;
}
.kix-page {
  filter: brightness(0.88) invert(100) hue-rotate(180deg);
}
.kix-cursor {
  filter: invert(100);
}
.kix-appview-editor {
  background-color: #282828;
}
.kix-page-paginated {
  box-shadow: 0 0 0 0.75pt #fff, 0 0 3pt 0.75pt #fff;
}
.docs-horizontal-ruler {
  border-bottom: 1px solid #2a2a2a;
}
.docs-ui-unprintable {
  filter: brightness(0.88) invert(100) hue-rotate(180deg);
}
.kix-paginateddocumentplugin-compact-mode {
  box-shadow: 0 0 0 0.75pt #282828, 0 0 3pt 0.75pt #222;
}
}
