css:
@-moz-document domain("gnarlyguides.com") {
/* ============= Body ============= */
* {
	color: #fff !important;
}

body.custom-background {
	background-color: #555 !important;
	z-index: -1000 !important;
}

#page {
	background-color: #777 !important;
	border: 1px solid #444 !important;
	box-shadow: 0px 0px 6px #444;
}

#site-description,
#site-title a,
p,
h1,
h2,
h3,
h4,
h5,
h6 {
	color: #fff !important;
}

a,
span {
	color: #66ff33 !important;
}

blockquote {
	border-left: 3px solid #a3c87e !important;
	border-color: #a3c87e !important;
}

.hentry .entry-title a {
	color: #fff !important;
}

#pg-1464-2 > .panel-row-style {
	background-color: #777 !important;
}

#pg-1464-3 > .panel-row-style {
	background-color: #777 !important;
}

/* ============= Nav ============= */
.main-navigation a {
	color: #fff !important;
}

.main-navigation ul li ul {
	background-color: #999 !important;
}

.read-btn a:hover,
.single-page p a:hover,
.single-page p a:hover,
.previous a:hover,
.next a:hover,
.tags a:hover,
.fa.search-top:hover {
	color: #66ff33 !important;
}

#site-title a:hover,
.hentry .entry-title a:hover,
#comments i,
.comments-area .comment-author-link a:hover,
.comments-area a.comment-edit-link:hover,
.comments-area a.comment-permalink:hover,
.comments-area article header cite a:hover,
.entry-meta .fa,
.entry-meta a:hover,
.nav-next a,
.nav-previous a,
.next a,
.previous a {
	color: #66ff33 !important;
}

.main-navigation ul li ul li a,
.main-navigation ul li.current-menu-item ul li a,
.main-navigation ul li ul li.current-menu-item a,
.main-navigation ul li.current_page_ancestor ul li a,
.main-navigation ul li.current-menu-ancestor ul li a,
.main-navigation ul li.current_page_item ul li a {
	color: #fff !important;
}

.main-navigation li.menu-item-has-children > a::after,
.main-navigation li li.menu-item-has-children > a::after,
.main-navigation li.page_item_has_children > a::after,
.main-navigation li li.page_item_has_children > a::after {
	color: #fff !important;
}

a,
.big-slider .entry-title a:hover,
.main-navigation :hover,
.main-navigation li.menu-item-has-children:hover > a::after,
.main-navigation li.page_item_has_children:hover > a::after,
.main-navigation ul li ul li a:hover,
.main-navigation ul li ul li:hover > a,
.main-navigation ul li.current-menu-ancestor a,
.main-navigation ul li.current-menu-ancestor a::after,
.main-navigation ul li.current-menu-item a,
.main-navigation ul li.current-menu-item a::after,
.main-navigation ul li.current-menu-item ul li a:hover,
.main-navigation ul li.current_page_ancestor a,
.main-navigation ul li.current_page_ancestor a::after,
.main-navigation ul li.current_page_item a,
.main-navigation ul li.current_page_item a::after,
.main-navigation ul li:hover > a,
.main-navigation ul.menu li.current-menu-ancestor ul li.current-menu-item > a,
#secondary .widget li a,
#tertiary .widget li a,
.fa.search-top,
.widget_service_block h5 a:hover,
.single-post-content a,
.single-post-content .entry-title a:hover,
.single-header h2,
.single-page p a,
.single-service span i,
#colophon .copyright-info a:hover,
#colophon .footer-nav ul li a:hover,
#colophon a:hover,
.comment .comment-reply-link::before,
.comments-area article header .comment-edit-link::before,
.copyright-info ul li a:hover,
.footer-widgets-area a:hover,
.menu-toggle::before,
a#scroll-up i {
	color: #66ff33 !important;
}

.main-navigation .menu > ul > li.current_page_ancestor,
.main-navigation .menu > ul > li.current_page_item,
.main-navigation .menu > ul > li:hover,
.main-navigation ul.menu > li.current-menu-ancestor,
.main-navigation ul.menu > li.current-menu-item,
.main-navigation ul.menu > li:hover,
blockquote,
.services-header h2,
.slider-button:hover,
.portfolio-button:hover,
.call-to-action-button:hover,
.read-btn a:hover,
.single-page p a:hover,
.single-page p a:hover,
.read-btn a {
	border-color: #00cc00 !important;
}

.main-navigation ul.menu > li.current-menu-item,
.main-navigation ul.menu > li.current-menu-ancestor,
.main-navigation .menu > ul > li.current_page_item,
.main-navigation .menu > ul > li.current_page_ancestor,
.main-navigation ul.menu > li:hover,
.main-navigation ul.menu > li.focus,
.main-navigation .menu > ul > li:hover,
.main-navigation .menu > ul > li.focus {
	border-top: 2px solid #00cc00 !important;
}

/* ============= Buttons ============= */

.so-widget-sow-button-atom-d064999fbca7 .ow-button-base a,
.so-widget-sow-button-atom-35f55903afc1 .ow-button-base a {
	background: #008000 !important;
}

.so-widget-sow-button-atom-d064999fbca7 .ow-button-base a.ow-button-hover:hover,
.so-widget-sow-button-atom-35f55903afc1 .ow-button-base a.ow-button-hover:hover {
	background: #009900 !important;
}
.ample-button,
button,
input[type="button"],
input[type="reset"],
input[type="submit"],
.comments-area .comment-author-link span,
.slide-next,
.slide-prev,
.header-post-title-container,
.read-btn a:hover,
.single-service:hover .icons,
.moving-box a,
.slider-button:hover,
.portfolio-button:hover,
.call-to-action-button:hover,
.ample-button,
input[type="reset"],
input[type="button"],
input[type="submit"],
button {
	background: #008000 !important;
}
}
