.right-brand div {
    height: 0px;
    width: 0px;
    margin-left: 0px;
    background: transparent no-repeat scroll 0 0;
    background-position: right;
}
.left-brand {
    right: 50%;
    margin: 0 0px 0 0;
    width: 0px;
    background-color: #0e0e0e;
}
.sk5 {
    background: none;
}
body {
    background-color: rgb(20, 20, 20)!important;
    color: rgb(216, 217, 218)!important;
}
.text-additional, .text-additional a, a.text-additional {
    color: #ff0000;
}

.federation-nav   .container,
.popover-title,
a:focus{
	background-color:rgba(0,0,0,0);
}

#mangaPicture, #nextPicture {
	opacity: 1;
    border-radius: 0px;
}

a:visited,
.cattitle,
.forum-breadcrumb * {
    color: #ffffff!important;
    transition: 300ms;
}

a,
.js-link {
    color: #d8d5cc!important;
    transition: 300ms;
}

.nav > li > a:hover, .nav > li > a:focus{
background-color: #0d3654;
}

table {
	border-collapse: separate;
}

a:hover,
.js-link:hover,
#mangaBox > div.leftContent > table > tfoot > tr > th > span > a:hover,
.navbar-default .btn-link:hover,
