font-size: 15px;
color: #2c992b;
}
.chapter .sub-info .date-added {
margin-top: 8px;
color: #a39c81;
}
.chapter-table-link .video-info {
font-size: 20px;
margin-bottom: 10px;
margin-top: 10px;
cursor: pointer;
color: #a39c81;
}
.card {
position: relative;
display: flex;
flex-direction: column;
min-width: 0;
word-wrap: break-word;
background-color: #25272b;
background-clip: border-box;
border: 1px solid rgba(0, 0, 0, 0.125);
