css:
@-moz-document url-prefix(https://cda.chronomania.net) {

body {
    background-color: black;
}
#top {
    display:block;
    color :red;
    background-color: black;
}
#top .left {
    display:block;
}
#top img {
    display:none;
}
#top .left {
    background-color: black;
    color :red;
    font-size:large;
}
#top .right .usermenu {
    background-color: black;
    color :red;
    font-size:large;
}
p.usermenu {
    margin:0;
}
#top div.right {
    margin: 0;
    padding: 0;
    text-align: right;
}
#top .right .usermenu a {
    background-color: black;
    color :red;
    font-size:10px;
}
#top .left .index {
    background-color: black;
    color :red;
}
#top .left .index a {
    background-color: black;
    color :red;
    font-size:15px;
}
#top .right .usermenu {
    display:block;
    background-color: black;
    color :red;
    font-size:15px;
}
#top .right .search {
    display:block;
    background-color: black;
    color :red;
    font-size:15px;
    font-style:italic;
}
#topnav {
    display:none;
}
#content {
    background-color: black;
    color: red;
    text-decoration: none;
    padding:0;
}
#content br {
    display: none;
}
#content p {
    background-color: black;
    color: red;
    text-decoration: none;
}
#content .postingheadline {
    background-color: black;
    color: red;
    text-decoration: none;
}
#content .posting {
    background-color: black;
    color: red;
    text-decoration: none;
}
#content a {
    color: red;
    text-decoration: none;
    font-size:large;
}
#content a.threadnew {
    background-image:none;
}
#content a.thread {
    background-image:none;
}
#content ul li img {
    display:none;
}
#content .postingbottom {
    display:none;
}
#bottomnav {
    background-color: black;
    color: red;
    position: fixed;
    left: 0;
    bottom: 0;
    width: 100%;
    text-align: center;
}
#bottomnav a {
    color: red;
    text-decoration: none;
}
#bottom {
    display:none;
}

}
