css:
@-moz-document domain("fhsched.com") {
body{
    background-color: #fff;
    background-image: none;
}
.header{
    border-bottom: #000;
    height: 55px;
    background-color: #000;
}
.header a{
    font-family: "Impact";
    background-color: #000;
}
.nav-bar a{
    font-family: "Impact";
}
.nav-bar{
    background-color: #000;
}
.period-display{
    font-family: "Impact";
}
.time-schedule{
    background-color: #fff0;
    font-family: "Impact";
}
.btn{
    background-color: #fff0;
    color: lightgrey;
}
.btn:hover{
    background-color: #fff0;
    color: darkgrey;
}
.dot{
    background-color: lightgrey;
}
.dot:hover{
    background-color: darkgrey;
}
.dot-cur{
    background-color: #000 !important;
}
.remaining-time{
    font-family: "Impact";
}
.splash{
    background-color: #fff0;
    font-family: "Impact";
}
footer{
    background-color: #fff0;
}
footer .red-footer{
    background-color: #fff0;
}
.wrongLunchBtn{
    background-color: lightgrey;
    color: #000;
    font-family: "Impact";
}
.wrongLunchBtn:hover{
    background-color: darkgrey;
    color: #000;
}
.lunchBtn{
    background-color: lightgrey;
    color: #000;
    font-family: "Impact";
}
.lunchBtn:hover{
    background-color: darkgrey;
    color: #000;
}
body.dark{
    background-color: #000;
    background-image: none;
}
body.dark .header{
    border-bottom: #000;
    height: 55px;
    background-color: #000;
}
body.dark .header a{
    font-family: "Impact";
    background-color: #000;
}
body.dark .nav-bar a{
    font-family: "Impact";
    color: #fff;
}
body.dark .nav-bar a:hover{
    color: darkgrey;
}
body.dark .nav-bar{
    background-color: #000;
}
body.dark .period-display{
    font-family: "Impact";
}
body.dark .time-schedule{
    background-color: #fff0;
    font-family: "Impact";
}
body.dark .btn{
    background-color: #fff0;
    color: lightgrey;
}
body.dark .btn:hover{
    background-color: #fff0;
    color: darkgrey;
}
body.dark .dot{
    background-color: lightgrey;
}
body.dark .dot:hover{
    background-color: darkgrey;
}
body.dark .dot-cur{
    background-color: #fff !important;
}
body.dark .remaining-time{
    font-family: "Impact";
}
body.dark .splash{
    background-color: #fff0;
    font-family: "Impact";
}
body.dark footer{
    background-color: #fff0;
}
body.dark footer .red-footer{
    background-color: #fff0;
}
body.dark .wrongLunchBtn{
    background-color: lightgrey;
    color: #000;
    font-family: "Impact";
}
body.dark .wrongLunchBtn:hover{
    background-color: darkgrey;
    color: #000;
}
body.dark .lunchBtn{
    background-color: lightgrey;
    color: #000;
    font-family: "Impact";
}
body.dark .lunchBtn:hover{
    background-color: darkgrey;
    color: #000;
}
}
