css:
@-moz-document domain(sfu-kras.ru) {

/***** общий layout *****/

#main-slider, .slogan, #fast-nav, .banners, .footer-bottom, #navigator, #lang-select, #side-menu-switch, #header-visual, #additional-menu .special, #footer .info, #header-links .visible-min-desktop, #header-links .single {
    display: none !important;
}

.node-page-1560 .banners {
    display: block !important;
}

#side-menu {
    margin-top: 10px !important;
}

#side-fast-nav {
    top: 200px;
    display: none;
}

#special-overlay, .bottom-back:before, body.with-side-fast-nav:before {
    background: white !important;
}

#breadcrumbs, #breadcrumbs .items, #breadcrumbs .items > .item > a {
    background: white;
}

h2 {
    margin-top: 10px;
    font-size: 25px;
}

h3, #news .news-item-title {
    font-size: 18px;
}

#main-menu .subitem-link {
    padding: 7px 17px;
}

#additional-menu a[href="http://bik.sfu-kras.ru/"] {
   font-weight: bold;
   color: #fb651f;
   animation-duration: 3000ms;
   animation-name: blink;
   animation-iteration-count: infinite;
   animation-direction: alternate;
}

@keyframes blink {
   from {opacity: 1; color: #fb651f;}
   to {opacity: 0.7; color: red;}
 }

/***** главная *****/

/* новости */

#home-news {
    margin-top: 0px;
}

#home-news-items .item {
    padding-top: 0px;
}

#home-news-items xxx.item-image, #home-news-items .item-category, #home-news-items .item-date {
    display: none;
}

/* анонсы */

#home-announcements h3.item-title, #home-news h3.item-title {
    font-size: 17px !important;
    line-height: 20px;
    margin: 5px 0px !important;
    padding-bottom: 0 !important;
    font-family: tahoma;
}

#home-announcements .item {
    height: 180px !important;
}

#home-announcements-rubricator, #home-announcements .item-image {
    display: none;
}

/* объявления */

#home-ads-items .item, #home-ads-items .item h3 {
    height: 40px;
}

#home-ads-items .item time {
    display: none !important;
}

#home-ads .item-link {
    padding-left: 20px;
}

#home-ads-items .item h3 {
    border-bottom: 1px solid #eee !important;
}

}
