css:
@-moz-document domain("duckduckgo.com") {
span.header__logo.js-logo-ddg {
    background-image: url(https://i.imgur.com/Jt50ZHo.jpg);
}

a.result__a {
    color: transparent !important;
    display: inline-block;
    position: relative;
}

a.result__a:before {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    white-space: nowrap;
    overflow: hidden;
    -ms-text-overflow: ellipsis;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    content: "Pika pika, pika pika.. pika, pika pika! Pika pika pika pika pika pik!";
    color: #00278e;
}

a.result__a:hover:before {
    text-decoration: underline;
}


}
