css:
@-moz-document domain("hackernoon.com") {
:root {
    --d-light: #b7b7b7;
    --d-dark: #3b3b3b;
    --d-brown: #7d6667;
    --d-tan: #cfbd97;
    --d-white: #f4f0e3;
}
#header {
    background-color: var(--d-light)!important;
}
#header i {
    color: var(--d-dark);
}
#header button.outline {
    color: var(--d-dark);
    border-color: var(--d-dark);
}
.logo,
.signup-image {
    filter: saturate(0)
}
.subnav {
    background-color: var(--d-brown);
}
.subnav .section .heading,
.more.featured .story-card a.tag {
    border-color: var(--d-dark);
}
.subnav .section .heading .line {
    background: var(--d-dark)
}
.subnav .section .heading::before,
.subnav .section .heading::after {
    background: var(--d-dark)!important
}
.subnav a,
.sponsor {
    color: var(--d-dark);
}
.subnav .dropdown {
    background-color: var(--d-brown);
}
.sponsor.secondary,
.sectionLayout--fullWidth img {
    filter: saturate(0);
}
.sectionLayout--outsetRow {
    display: flex;
}
.story blockquote {
    border-left: 0px;
}
.content a {
    filter: hue-rotate(260deg) saturate(0.3);
}
.story-card:hover .img {
    background-color: var(--d-light);
}
.submit-btn .button,
.story-card a.tag {
    background: var(--d-light);
    border-color: var(--d-dark);
    color: var(--d-dark)
}
.checkbox input[type="checkbox"]:focus + label::before {
    outline-color: var(--d-light)
}
.divider-title a::before,
.divider-title a:hover::before {
    background: var(--d-light)
}
#about.about .boxes button.active,
#about.about .message {
    background-color: var(--d-brown);
    border-color: var(--d-dark);
    color: var(--d-white);
}
.about .message .cta a.button {
    border-color: var(--d-white);
    color: var(--d-white);
}
}
