css:
@-moz-document url-prefix("https://linuxacademy.com/cp/courses/"), url-prefix("https://app.linuxacademy.com") {
/* linuxacademy.com DARK MODE */

body {
    background-color: #182b37;
    color: #ddd;
}

.video-lesson-dashboard {
    border: 2px dotted #29485B;
}

.video-header {
    border-right: none !important;
    border-bottom: 2px dotted #29485B !important;
}

.video-menu-header {
    border-bottom: 2px dotted #29485B !important;
    border-left: 2px dotted #29485B !important;
}

.video-menu-header li {
    color: #eee !important;
}

.video-lesson-container h2, .video-lesson-container  h3 {
    color: #92d5a2;
}

.course-name {
    color: #eee !important;
}

.video-details a {
    color: #8fb0d9 !important;
}

.btn-la-major {
    font-weight: bold;
    background: #48bb63 !important;
}

.btn-la-major-grey {
    color: #444 !important;
}

.vl-placeholder-text {
    color: #eee;
}

.add-card-btn {
    color: #eee !important;
    font-weight: bold;
}

.pop-decks {
    color: #8fb0d9 !important;
    font-weight: bold;
}

.pop-cards {
    color: #eee !important;
}

.sidebar-heading {
    border-bottom: none !important;
}

.vl-deck {
    border: 1px solid #29485B !important;
    background: #1f3f53 !important;
}
.vl-deck:hover {
    border: 1px solid #203a4a !important;
    box-shadow: none !important;
}

.deck-list-holder:after {
    background-image: none !important;
}

.lab-show-container .mat-card {
    background: #29485B;
}
.lab-show-container .mat-card h1, .lab-show-container .mat-card a {
    color: #ddd !important;
}

.lab-show-container .mat-card-primary .mat-card-title {
    background: #ddd !important;
}

.lab-show-container .mat-expansion-panel {
    background: #29485B;
    color: #ddd !important;
}

.lab-show-container .mat-expansion-panel h2 {
    color: #23c6a9 !important;
}

.lab-show-container .mat-expansion-panel p {
    color: #aaa !important;
}

.lab-show-container .mat-expansion-panel-header h1 {
    color: #ddd !important;
}

.lab-show-container .mat-expansion-panel-header .mat-expansion-panel-header-title {
    color: #ddd !important;
}

.lab-show-container .mat-expansion-panel-header p {
    color: #aaa !important;    
}

.lab-show-container .mat-expansion-panel-header-description {
    color: #aaa !important;
}

.lab-show-container .mat-expansion-indicator::after {
    color: #ddd;
}

.lab-show-container .mat-expansion-panel-content h1 {
    color: #ddd !important;
}

.lab-show-container .mat-expansion-panel-content h2 {
    color: #eee !important;
} 

.lab-show-container .description-content p{
    color: #ddd !important;
}

.lab-show-container .lab-title {
    color: #ddd !important;
}

.lab-show-container .lab-times {
    color: #aaa !important;
}

.lab-show-container a.difficulty {
    color: #23c6a9 !important;
}

.lab-content-container h1, .lab-content-container .lab-content-container h2, .lab-starting-container h1, .lab-show-container .lab-starting-container h2 {
    color: #ddd;
}

.lab-detail-container h1 {
    color: #ddd !important;
}

.cat-icon-label {
    color: #23c6a9 !important;   
}

.lab-detail-container a {
    color: #23c6a9;
}

.lab-show-container h2 {
    color: #ddd !important;
}














}
