css:
@-moz-document domain("nebenan.de") {

:root {
    --Accent-color: #D53259;
    --Text-color: #35A69E;
    --Darker-Text-color: #4D6475;
    --Background-color: #1E2C39;
    --Icon-Color: #D53259;

}

/* HTML Background */
.c-layout-wrap {
    background: none !important;
    background-color: var( --Darker-Text-color) !important;
    color: var(--Accent-color) !important;
}

/* Basics */
.body,
.main,
.c-layout_header_panel,
.c-layout_header_panel-content,
.c-layout_header_panel-label_profile,
.c-logo-text,
.icon-thumbsup,
.c-feed_item_actions-extra_label,
.c-feed_marketplace,
.c-feed_item_actions-list,
.c-layout_menu_footer,
.icon-logo,
.c-layout-container
.c-feed_preview-container,
.c-slideshow,
.c-feed_slideshow,
.c-slideshow-draggable,
.c-slideshow-list,
.is-animated,
.on-logo_type_de_de,
.c-donations_banner,
.c-feed_preview-header,
.ui-text-light,
.ui-media,
.c-layout_menu-banner,
.c-layout_footer,
.c-logo,
.c-layout_header_panel-item,
.c-feed_slideshow-content-wrapper,
.c-feed_slideshow-title,
.ui-card-section,
.c-feed_slideshow-author,
.footer,
.header
{
    background: none !important;
    background-color: var(--Background-color) !important;
    color: var(--Text-color) !important;
}

/* Text */
    /* Header */
.c-rich_content,
.c-expandable_rich_content,
.c-rich_content-content,
.c-comment_list-item,
.c-tag_cloud-list,
.c-comment-creator_message,
.c-feed_event-show_details,
.c-context_menu,
.c-context_menu-label,
.c-comment-actions,
.c-comment-header-content,
.c-comment-content,
.c-comment-time,
.c-emoji,
.c-editable_section,
.ui-card-section,
.c-profile_info,
.is-empty,
.c-editable_section-header,
.ui-small
            {
             color: var(--Accent-color) !important;
            background-color: var(--Background-color) !important;
            }
.c-content_header
{
            color: var(--Accent-color) !important;
            background: none !important;
            }
    /* Blocktext */ 
.ui-h2,
.ui-card,
.ui-label,
.c-rich_content,
.c-expandable_rich_content
.c-user_name,
.c-feed_marketplace-body,
.c-invite-section,
.c-rich_content-content,
.c-feed-description-label
            {
              color: var(--Text-color) !important;
            }
   /* Dnukler Text */ 
            .ui-text-dark
            {
              color: var(--Darker-Text-color) !important;
            }

/* Deko Header */
.c-feed_item_header-content,
.icon-i_circle,
.icon-cross,
.c-feed_item_header-category,
.c-dropdown-close
            {
            color: var(--Background-color) !important;
            background-color: var(--Text-color) !important;
            }
/* Buttons */
.ui-button,
.ui-button-round,
.ui-button-small,
.ui-button-secondary,
.c-tag_cloud-item, 
.c-toggle,
.c-toggle-control,
.checkbox,
.c-toggle-container,
.has-label,
.ui-tag,
.ui-tag-secondary,
.ui-button-primary,
.c-message_form-button
{
    color: var(--Accent-color) !important;
    background-color: var(--Background-color) !important;
    }

/* Slider-Buttons */
.c-toggle-slide
{
    color: var(--Accent-color) !important;
    background-color: var( --Accent-color) !important;
    }
/* Drop-Down-Menus */
.c-dropdown-option,
.c-context_menu,
.c-layout_menu_list,
.c-dropdown 
.c-feed_item_options,
.c-dropdown-label,
.icon-arrow_down,
.is-top,
.c-context_list-item
{
    background: none !important;
    background-color: var(--Background-color) !important;
    color: var(--Text-color) !important;
}

/* Antworten */
.ui-input,
.c-textarea-container,
.c-textarea_wrap-panel,
.c-context_menu,
.c-dropdown,
.c-emoji_cloud,
.c-tooltip, 
.c-tooltip-top
            {
    color: var(--Text-color) !important;
    background-color: var(--Background-color) !important;
            }

/* Seitenleiste */
.is-active,
.c-layout_menu_footer,
.c-donations_banner,
.ui-media-content,
.ui-media,
.c-layout_menu-banner,
.c-layout-sidebar-content,
.c-layout_menu_list-item
{
    background: none !important;
    background-color: var(--Background-color) !important;
    color: var(--Text-color) !important;
}

/* Einstellungen */
.c-tab_scroller-list,
.c-tab_scroller-item,
.c-privacy-visibility_option,
.is-active,
.is-clickable
{
    background: none !important;
    background-color: var(--Background-color) !important;
    color: var(--Text-color) !important;
}
/* Progress-bar im Profil */
.c-progress,
.c-progress-primary
{
    background: none !important;
    background-color: var(--Darker-Text-color) !important;
    color: var(--Text-color) !important;
}
.c-progress-state {
    background: none !important;
    background-color: var(--Text-color) !important;
    color: var(--Background-color) !important;
}

/* Text-Links */
.ui-link
{
    color: var(--Darker-Text-color) !important;
    background-color: var(--Background-color) !important
    }
.ui-link:hover
{
    color: var(--Accent-color) !important;
 
    }

/* h-ref */
a {
    color: var(--Darker-Text-color) !important;
    background-color: var(--Background-color) !important
}

a:hover {
    color: var(--Accent-color) !important;
    
    }

h1 {
    color: var(--Accent-color) !important;
    background-color: var(--Background-color) !important
}

h2{
    color: var(--Text-color) !important;
    background-color: var(--Background-color) !important
}

.ui-h3{
    color: var(--Text-color) !important;
    background-color: var(--Background-color) !important
}

h3{
    color: var(--Text-color) !important;
    background-color: var(--Background-color) !important
}


}
