css:
@-moz-document domain("rockpapershotgun.com") {
/* site wide */

.header-desktop {
    background-color: #1A1A1A;
}

.header-desktop .search form input {
    background-color:#1A1A1A;
}

body {
    background-color: #1A1A1A;
}

.page>main {
    background-color: #1A1A1A;
}

.footer {
    background-color: #1A1A1A;
}

/* main page */

.blog-post {
    background-color: #FFFFFF;
}

/* articles / features */

.article header .breadcrumb a {
 color:#C0C0C0;
 text-decoration:none
}

.title {
     color: #FFBF00;
}

.title a {
     color: #FFBF00;
}

.article>main {
    background-color: #1A1A1A;
    color: #FFFFFF;
}

.article>main h3 {
    color: #FFBF00;
}

/** metadata **/
.article header .metadata .author-name a {
    color: #FFBF00;
}

.metadata p.author-job-title {
    color: #FFFFFF;
}

.metadata p.publication-date {
    color: #C0C0C0;
}

/** details **/

.details p.name {
    color: #FFBF00;
}

.about-the-author p.section-title {
    color: #FFFFFF;
}

.details p.job-title{
    color: #FFFFFF;
}

.details p.description {
    color: #FFFFFF;
}

/** comments **/
.comment.subscriber {
    color:#C0C0C0;
}

.username {
     color: #FFBF00;
}

.comment.subscriber>.details .date a {
    color:#C0C0C0; 
}

.comment.administrator>.details .date a {
    color:#C0C0C0; 
}

}
