css:
@-moz-document url-prefix("https://discordapp.com/channels/"), url-prefix("https://discord.com/channels/") {
/* open close server button */

div[class*="base-"]{
    left:5px;
}
nav[class*="wrapper-"]:hover +div[class*="base-"]{
    left:72px;
}




/* open close sidebar panel */
div[class*="base-"] div[class*="content-"] div[class*="sidebar-"]{
    width:80px; 
    overflow-x: hidden;
    overflow-y: hidden;
    resize: horizontal;	
}


div[class*="containerDefault-"] div[class*="iconVisibility-"] div[class*="content-"]{
    margin: 1px;
    padding:1px;
    border-radius:0;
    height:auto;
}
div[class*="containerDefault-"] div[class*="iconVisibility-"] div[class*="content-"] svg{
    width:16px;
    height:16px;
}
div[class*="containerDefault-"] div[class*="wrapper-"] {
    height:auto;
}
div[class*="content-"] div[class*="name-"]{
    font-size:1em;
}
