    padding:0px 3px;
}

div[class*=""]
*/

div[class*="containerDefault"] div[class*="list"]{
	padding-left:0px;
    padding-bottom:0px;
}


/* compact user list */
div[class*="membersWrap-"]{
    min-width:160px;
}
div[class*="membersWrap-"] div[class*="members-"]{
    max-width:160px;
}
div[class*="member-"] div[class*="layout-"]{
    height:auto;
    padding:0px;
    border-radius:0;
}
/*
.container-2Pjhx-{
    padding:0px;
    border-radius:0;
}
*/

div[class*="membersGroup-"]{
    padding: 10px 0px 0px 5px;
    height:auto;
}
div[class*="members-"] div[class*="layout-"] div[class*="avatar-"]{
    margin-right:0px;
}

div[class*="nameAndDecorators-"] div[class*="name-"]{
    font-size:1em;
}





