div[class*="cozyMessage-"][class*="message-"]{
	padding: 2px 0px !important;
    margin-top:0px !important;
    margin-right:30px !important;
    left:30px;
	right:0px;
}


div[class*="contents-"] img[class*="avatar-"]{
	margin:0px;
    width: 24px;
	height: 24px;
    border-radius:30%;
	left:-28px;
}



div[class*="messagesWrapper-"] + form{
	    margin-left: 0px;
        margin-right: 0px;
    padding-left:0px;
    padding-right:0px;
}

div[class*="attachWrapper-"] button{
	padding:0px;
}
div[class*="buttons-"] div[class*="contents-"],
div[class*="buttons-"] button{
	margin-right:0px;
    margin-left:0px;
    padding:0px;
}

div[class*="flex"] div[class*="channelTextArea"]{
    padding-top:0px;
}

div[class*="markup"],
div[class*="embedDescription"]{
    line-height:1.1;
}
    
}
