css:
@-moz-document domain("online.s-pankki.fi") {
html, body
{
    background-color: #111;
    color: #ccc;
}

p { color: #c4c4c4; }
h2, label,
ul.tabNavigation li a,
.breadcrumbs li a,
.tooltip-trigger,
ul#footer li a
{
    color: #ccc;
}

body > .cf-header, .main-container,
.cf-container, .cf-header > .content, .main-container
{
    border: .0625rem solid #555;
}
.navigation  { border-bottom: 1px solid #555; }
.navigation ul.level-1 > li.act > a, .navigation ul.level-1 > li.js-open > a, .no-js .navigation ul.level-1 > li:hover > a
{
    border: 1px solid #555;
}

.cf-header > .content,
.cf-content, .main-container
{
    background-color: #333;
}

.c_message.c_message--info,
div.error, p.error,
div.info, p.info
{
    background-color: #131617;
}

[type="color"], [type="date"], [type="datetime-local"], [type="datetime"], [type="email"], [type="month"], [type="number"], [type="password"], [type="search"], [type="tel"], [type="text"], [type="time"], [type="url"], [type="week"], textarea,
[type="color"]:focus, [type="date"]:focus, [type="datetime-local"]:focus, [type="datetime"]:focus, [type="email"]:focus, [type="month"]:focus, [type="number"]:focus, [type="password"]:focus, [type="search"]:focus, [type="tel"]:focus, [type="text"]:focus, [type="time"]:focus, [type="url"]:focus, [type="week"]:focus, textarea:focus,
select
{
    background-color: #1a1a1a;
    color: white;
}

.c_box__header,
.c_box.c_box--frontpage .c_box__content,
table.data,
.c_box--asko .c_box__content, .c_box--bonus .c_box__content,
.c_box__content--cms,
.c_box__bottom--cms,
.mega.mega-2,
.navigation ul.level-1 .megaDynamic,
.navigation ul.level-1 li .megaLinks li a,
.c_box__content,
table.def-table tr.even, table.def-table tr.odd, table.def-table tr:nth-child(2n), table.def-table tr:nth-child(2n+1),
.c_box__bottom,
.c_box__bottom.c_box__bottom--pinCode,
.tabs .top-line,
.transfer-of-salary .bg-white,
.transfer-of-salary .accoH
{
    background-color: #222;
    color: #ccc;
}
fieldset.highlight,
.tabContent.txt
{
    background-color: #222 !important;
}
.compact-form .highlight
{
    background-color: #33392e;
}
ul.tabNavigation li a
{
    background-color: #111;
}
ul.tabNavigation li a.selected
{
    background: linear-gradient(#111,#222);
}
ul.tabNavigation li a.selected span { color: white; }

.navigation ul.level-1 li .megaLinks li a,
body.default-skin .navigation ul.level-1 li .megaLinks ul.sub li a
{
    background: none;
}
.c_box__header h2,
.navigation ul.level-1 li .megaLinks ul.sub li a span
{
    color: #ccc;
}
table.data th a
{
    color: white;
}

.c_box--asko .c_box__header, .c_box--bonus .c_box__header
{
    background: linear-gradient(#5b8f22,#5b8f22);
}

.navigation ul.level-1 > li.act a,
.navigation ul.level-1 > li.js-open > a
{
    background-color: #222;
}
.navigation ul.level-1 .megaDynamic { margin: 0; }

.data tr:nth-child(2n), .even {
    background-color: rgba(255,255,255,.05);
}

table.grand-summary thead { border-top: none; }

fieldset.normal-form.wide select, fieldset.normal-form select
{ background: none; }
}
