css:
@-moz-document domain("basis.uni-bonn.de") {
/*
Style written by Leon Stolze (2019)
For problems, suggestions or other, you can email me: s6lestol@uni-bonn.de
*/



/*bg, dark*/
html, #wrapper, .divcontent, .portalcontentAnz, .background, .links2visited{
    background: #2a2a2e !important;
    /*border-bottom: 1px solid #1d1d1d !important;*/
}

/*bg, lighter*/
.content_max_portal_qis, #makronavigation, .services, .divloginstatus, .content, .mikronavi_list, .mod_n_odd, .abstand_veranstaltung, td.klein{
    background: #38383d !important
}

/*bg, lightest*/
.divlinks, fieldset, legend, .mod, .mod_header, .mod_modul, .mod_grid, .mod_n_basic, .mod_n, .qiszul_button, .functionnavi, .tabelle1, .mod_n_even, ul, .mikronavi, td.td, td.th  {
    background-color: #4a4a51 !important
}

/*tables*/
.tabelle1, .tabelle1_alignleft, .tabelle1_aligncenter, .tabelle1_alignright {
    color: white;
    border: 1px solid white;
}


/*font color*/
td, span, div {
    color:#FFFFFF !important;
}

/*font colour warnings*/

.normal, font {
    color:red !important
}

/*writeable fields*/
.mod, .mod_header, .mod_modul, .mod_grid {
    border-left: white 1px solid;
    border-bottom: white 1px solid;
    border-right: white 1px solid;
}


/*switches the university logo for a better suited version, unfinished*/
#logo img {
    width:0px; 
    height:0px; 
    padding-top: 0px; 
    padding-bottom: 64px; 
    padding-right: 32px; 
    padding-left: 32px; 
    background: url(https://www.google.com/url?sa=i&rct=j&q=&esrc=s&source=images&cd=&ved=2ahUKEwiwqebf-9HkAhXCbFAKHdlRDgkQjRx6BAgBEAQ&url=https%3A%2F%2Fwww.linkedin.com%2Fschool%2Frheinische-friedrich-wilhelms-universit%25C3%25A4t-bonn%2F&psig=AOvVaw1yN5GMgtZEfThsMtcsvsHG&ust=1568607311116609);
}

.logo {
    display:none;
}


/*links*/
a {
    color:#FFFFFF
}

a:link {
    color: lightgreen !important;
}

a:visited {
    color: green !important;
    background-color: lightgreen !important;
}

a:hover {
    color: red !important;
}

a:active {
    color: pink !important;
}

/*
    Notes:

#ababae light grey
*/
}
