css:
/* i really want this to be global */

@-moz-document domain("cashbox.ru") {

.cashbox-admin_tasks_content .table tr td {
    padding: 0px 8px;
    vertical-align: middle;
    
}


    
    body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 2.22857143;
    color: #333;
    background-color: #fff;
}
.sidebar-menu>li>a {
    padding: 2px 5px 2px 5px;
    display: block;
}

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    padding: 10px;
    line-height: 1.32857143;
    vertical-align: top;
    border-top: 1px solid #ddd;
}

.cashbox-admin_tasks_content .table tr td {
    border-bottom: 1px solid #9d9d9d;
}

.skin-green .name_task {
    color: #4f6c8c;
}
.bg-green, .callout.callout-success, .alert-success, .label-success, .modal-success .modal-body {
    background-color: #1b5271 !important;
}
.skin-green .main-header .navbar {
    background-color: #6c9dae;
}
.skin-green .main-header .logo {
    background-color: #6c9dae;
    color: #fff;
    border-bottom: 0 solid transparent;
}
.btn-success {
    background-color: #6c9dae;
    border-color: #777777;
}
.skin-green .treeview-menu>li.active>a, .skin-green .treeview-menu>li>a:hover {
    color: #fff;
}
.skin-green .sidebar a {
    color: #b8c7ce;
}
.skin-green .treeview-menu>li>a {
    color: #8aa4af;
}
.skin-green .treeview-menu>li>a {
    color: #8aa4af;
}
.cashbox-admin .sidebar-menu li>a, .cashbox-admin {
    font-family: "Roboto", sans-serif;
}
.skin-green .treeview-menu>li.active>a, .skin-green .treeview-menu>li>a:hover {
    color: #fff;
}

.cashbox-contractor .cashbox-admin_tasks_content .table tr td:last-child {
    text-indent: 0;
    vertical-align: top;
    padding-top: 11px;
}
  
}
