/* ==UserStyle==
@name         Tofugu Dark
@version      1.0.5
@description  Dark theme for Tofugu
@namespace    AfroThundr3007730
@author       AfroThundr
@homepageURL  https://github.com/AfroThundr3007730/Tofugu-Dark
@supportURL   https://github.com/AfroThundr3007730/Tofugu-Dark/issues
@updateURL    https://raw.githubusercontent.com/AfroThundr3007730/Tofugu-Dark/master/tofugu-dark.user.css
@license      GPL-3.0
==/UserStyle== */
@-moz-document domain("www.tofugu.com") {
  :root {
    --gray-2: #222;
    --gray-3: #333;
    --gray-7: #777;
    --gray-c: #ccc;
    --gray-18: #181818;
    --white: #fff;
  }
  body, .navbar-light .navbar-nav .nav-link, .fa {
    color: var(--gray-c);
  }
  footer.document a:not(:hover) {
    color: var(--gray-c) !important;
  }
  .navbar-light .navbar-nav .nav-link:focus,
  .navbar-light .navbar-nav .nav-link:hover {
    color: var(--white);
  }
  code, .blog-post-highlight, .individual-review {
    background-color: var(--gray-18);
  }
  body, .search-area, .bg-angled, .article .article-content, hr::after {
    background-color: var(--gray-2);
  }
  .navbar .nav-item.search, .bg-faded, footer.document, footer.document ul li a,
  footer.document ul li.copyright span, footer.document ul li.heading span,
  div[class] ol.article-index-list li a:hover, .table-hover tbody tr:hover,
  .table-striped tbody tr:nth-of-type(2n+1) {
    background-color: var(--gray-3);
  }
  .table-bordered th, .table-bordered td, .table th, .table td, .table thead th {
    border-color: var(--gray-7);
  }
  body.index .additional-posts h2 .highlight {
    background-color: transparent;
  }
  img {
    background-color: var(--white);
    filter: brightness(60%);
  }
  img:hover {
    filter: brightness(100%);
  }
}
