/* ==UserStyle==
@name         KaniWani Dark
@version      1.0.3
@description  Dark theme for KaniWani
@namespace    AfroThundr3007730
@author       AfroThundr
@homepageURL  https://github.com/AfroThundr3007730/KaniWani-Dark
@supportURL   https://github.com/AfroThundr3007730/KaniWani-Dark/issues
@updateURL    https://raw.githubusercontent.com/AfroThundr3007730/KaniWani-Dark/master/kaniwani-dark.user.css
@license      GPL-3.0
==/UserStyle== */
@-moz-document domain("kaniwani.com") {
  :root {
    --gray-1: #111;
    --gray-2: #222;
    --gray-3: #333;
    --gray-4: #444;
    --gray-5: #555;
    --gray-7: #777;
    --gray-c: #ccc;
    --lesson-1: #345;
    --lesson-2: #456;
    --review-1: #303;
    --review-2: #404;
    --tag-1: #244;
    --tag-2: #434;
    --tag-3: #423;
    --tag-4: #424;
    --tag-5: #442;
    --tag-6: #473;
    --slide: #224;
  }
  body {
    background-color: var(--gray-1);
    color: var(--gray-c);
  }
  button, input, select, textarea {
    background-color: var(--gray-1);
    border-color: var(--gray-5);
    color: var(--gray-c);
  }
  .hzoTof {
    background-color: rgba(0, 0, 0, .1);
  }
  .ihuegB > input, .eStPfI, .hqSSFE, .fcFRGf {
    background-color: var(--gray-1);
  }
  .cTRZKg, .cXDhnt, .hZnLNP, .gitBaj, .eJDrkt {
    background-color: var(--gray-2);
  }
  .kqQGEc, .fuoHfy, .cCmHrU {
    background-color: var(--gray-3);
  }
  .eoGMIh {
    background-color: var(--gray-2);
    background-image: linear-gradient(var(--gray-3), var(--gray-2));
  }
  .fTVupX {
    background-color: var(--review-2);
    background-image: linear-gradient(var(--review-2), var(--review-1));
  }
  .cGyjlT {
    background-color: var(--lesson-2);
    background-image: linear-gradient(var(--lesson-2), var(--lesson-1));
  }
  .fjuPmy {
    background-color: var(--tag-1);
    color: var(--gray-7);
  }
  .euTLPg {
    background-color: var(--tag-2);
    color: var(--gray-7);
  }
  .bFODhT {
    background-color: var(--tag-3);
    color: var(--gray-7);
  }
  .bDnISH {
    background-color: var(--tag-4);
    color: var(--gray-7);
  }
  .cVOIHq {
    background-color: var(--tag-5);
    color: var(--gray-7);
  }
  .hZnLNP .mae4y7-0 {
    background-color: var(--tag-6);
  }
  svg:not([fill]) {
    fill: var(--gray-c);
  }
  rect[fill="#fff"] {
    fill: var(--slide);
  }
  .fuoHfy:not(:disabled):hover, .fuoHfy:focus {
    background-color: var(--gray-4);
    color: var(--gray-7);
  }
  .jMdDjV, .knkzyA > li {
    border-color: var(--gray-5);
  }
  .iCjPNG, .dVreRX, .ebbVcZ {
    border-bottom-color: var(--gray-2);
  }
  .bhcxot, .iTpaYB {
    background-color: var(--gray-2);
    color: var(--gray-7);
  }
  .fTVupX .st7o6u-1, .cGyjlT .st7o6u-1 {
    text-shadow: none;
  }
  .sUEkW {
    filter: invert(100%) hue-rotate(180deg);
  }
  .emyZim {
    filter: brightness(50%);
  }
}
