css:
.chat-line--me {
 padding-left: 2.8em;;
    padding-right: 0;
    text-align: left; 
}
.chat-line--me .chat-line__avatar {
	left: 0;
    right: auto;
}
.chat-line__bubble {
	background: none!important;
}
.chat-line__meta {
	font-size: 1em;
  	display: flex;
 align-items:center; 
}
.chat-line__author {
    display: inline-flex!important;
    text-transform: capitalize;
    margin-right: 0.5em;
}
.chat-line--me .chat-line__meta:after {
	display: none;
}
.chat-line__timestamp { 
	font-size: 0.8em;
}
.chat-line--me .chat-line__timestamp {
    margin-top: 0;
    margin-left: 0.5rem;
    display: inline-flex;
    align-items: center;
    flex-direction: row-reverse;
}
.chat-line--me .chat-line__timestamp .chat-line__toggle-delete-mode {
	height: 1.45em;
}
.chat-line--thread {
    padding-top: 0;
    margin-top: -0.875em;
}
.chat-line__trash {
	right: -2.2em;
    left: auto;
}
/* i really want this to be global */
