css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/*
Hide Left Titlebar Spacer (Firefox65)
*/
hbox.titlebar-spacer[type="pre-tabs"]
{
  display: none !important;
}


 #back-button > .toolbarbutton-icon
  {
    padding: 5px 7.4px 5px 5.4px !important;
border-width: 2.5px !important;
   border-color: rgba(205,205,205,100) !important;
  }

 #back-button:hover:not([disabled]) > .toolbarbutton-icon
  {
background-color: rgba(225,225,226,100) !important;
box-shadow: none !important;
   border-color: rgba(195,195,196,100) !important;
    padding: 5px 7.4px 5px 5.4px !important;
border-width: 2.5px !important;
  }

#urlbar
{
 font-size: 15px !important;
border-width: 2.5px !important;
   border-color: rgba(225,225,225,100) !important;
border-radius: 5px !important;
  padding-left: 3px !important;
  padding-right: 3px !important;
  box-shadow: none !important;
 background-color: rgba(240,240,240,100) !important;
}
#urlbar:hover
{
background-color: rgba(255,255,255, 100) !important;
}

#urlbar:focus-within
{
   background-color: rgba(255,255,255, 100) !important;
   border-color: rgba(10,110,240,100) !important;
}


#PlacesToolbarItems > .bookmark-item:not([type="menu"]):hover
{
  background-color: rgba(212,212,212,100) !important;
}

#PlacesToolbarItems > .bookmark-item:not([type="menu"]):active
{
   background-color: rgba(249,249,250,100) !important;
}

#PlacesToolbarItems > .bookmark-item[type="menu"]:hover
{
  background-color: rgba(220,220,220,100) !important;
}

#PlacesToolbarItems > .bookmark-item[type="menu"]:active
{
  background-color: rgba(249,249,250,100) !important;
}

#PlacesToolbarItems > .bookmark-item[type="menu"][open]
{
  background-color: rgba(204,204,204,100) !important;
}

#PlacesToolbarItems > .bookmark-item[type="menu"][open]:active
{
  background-color: rgba(204,204,204,100) !important;
}

#PlacesToolbarItems > .bookmark-item > .toolbarbutton-icon
{
  margin-left: 2px !important;
  margin-right: 6px !important;
  width: 16px !important;
  height: 16px!important;
}

/* ----------------------- */

#context-back image,
#context-forward image,
#context-reload image,
#context-stop image,
#context-bookmarkpage image,
#context-navigation,
#context-sep-navigation {
    display:none !important;
}
#context-savepage,
#context-pocket,
#context-viewbgimage,
#context-selectall,
#context-viewsource,
#context-viewinfo,
#context-sep-sendpagetodevice,
#context-sep-viewbgimage,
#context-sep-viewsource,
#context-openlinkinusercontext-menu,
#context-bookmarklink,
#context-savelink,
#context-savelinktopocket,
#context-viewpartialsource-selection,
#context-inspect-a11y,
#context-viewimage,
#context-viewimageinfo,
#context-sendimage,
#context-setDesktopBackground,
#context-openlink,
#context-openlinkprivate

{
    display:none !important;
}


menuitem,
menupopup,
splitmenu
{
  -moz-appearance: none !important;
  background-color: rgba(255,255,255, 100) !important;
}

menuitem:hover, menu[_moz-menuactive="true"]
{
  background: rgba(237,237,237, 100) !important;
}
tabs
{
 font-size: 13px !important;
}

menuitem .menu-accel-container
{
  padding-right: 9px !important;
}

menuitem
{
  min-height: 24px !important;
  margin-left: 0px !important;
  margin-right: 0px !important;
  padding-top: 3px !important;
  padding-bottom: 3px !important;
}
menu, menuitem, menucaption, .splitmenu-menuitem
{
  -moz-appearance: none !important;
  -moz-box-align: center;
  min-height: 22px !important;
  padding-top: 3px !important;
  padding-bottom: 3px !important;
  font-size: 13px !important;
  font-family: 'Segoe UI' !important;
  padding-left: 6px !important;
  padding-right: 6px !important;
}

menupopup
{
border-radius: 5px !important;
  border: 2px solid rgba(212,212,212, 100) !important;
  padding-top: 6px !important;
  padding-bottom: 6px !important;
  padding-left: 0px !important;
  padding-right: 0px !important;
}
