css:
@-moz-document url-prefix("http://www.smashcompany.com/") {
.post-details {
    display: none;
}
#right-column {
    display: none;
}
.post-content {
    font-family: Georgia, Cambria, "Times New Roman", Times, serif;
    color: rgba(0, 0, 0, 0.84);
    font-size: 18px;
    line-height: 1.58;
    max-width: 728px;
    margin: 0 auto;
    padding: 24px;
}
div#header {
    width: auto;
    margin-top: 0;
}

p#pagination {
    padding: 0;
    margin: 0;
}
h2.new-post
{
    padding: 5px 0;
    margin: 0;
}


div.left-column {
    float: none;
    width: auto;
}
div.post-content {
    float: none;
    width: auto;
    max-width: 100%;
    padding-top: 16px;
}
div.post-content h1 {
    font-size: 40px;
    line-height: 48px;
}
div.post-content h2 {
    font-size: 24px;
    margin-top: 1.23em;
}
div.post-content p {
    font-size: 18px;
    line-height: 1.58;
    margin-top: 1.56em;
    width: auto;
}

div.post-content a {
    text-decoration: underline;
    font-weight: normal;
}

div.post-content h1 a {
    text-decoration: none;
}
}
