css:
@-moz-document regexp("https:\/\/(?!(www|canvadocs)\.).+\.instructure\.com.*") {
/* Dumb fix */
.ToDoSidebarItem__Info {
    width: 0;
}

body:not(.full-width):not(.outcomes) .ic-Layout-wrapper {
    max-width: none;
}

/* Make this static */
.ic-Dashboard-header, body.dashboard-is-planner .ic-Dashboard-header {
    position: static;
}

/* Icon button on background */
.ic-DashboardCard__header-button {
    text-shadow: 0px 1px 5px black;
}
}
