/* === Variable Overrides === */
:root {
    --ic-brand-global-nav-ic-icon-svg-fill--active: #323639;
    --ic-brand-global-nav-menu-item__text-color: #323639;
    --ic-brand-global-nav-bgd: #323639;
    --ic-brand-font-color-dark-lightened-30: #ddd;
    
    --eHiXd-iconColor: hsl(0, 0%, 60%);
}
/* === END Variable Overrides === */


/* ==== General ==== */
:root {
    --background-color: hsl(0, 0%, 7%);
    --background-color-lighter: hsl(0, 0%, 12%);
    --brand-color: #323639;
    --link-color: #ddd;
    --link-color-secondary: #e8e8e8;
    --link-color-tertiary: #bbb;
       
    --text-color: hsl(205, 9%, 65%);
    --text-color-primary: #ddd;
    
    --button-color-background: #222;
    --button-color-border: #333;
}

body {
    color: var(--text-color);
}

#wrapper {
    background: var(--background-color);
}
/* === END General === */


/*
 * DASHBOARD PAGE
 */

/* === Dashboard Card === */
.ic-DashboardCard {
    background: var(--background-color-lighter);
}
.ic-DashboardCard__header_content {
    background: var(--background-color-lighter);
}
.ic-DashboardCard__header-title span {
    color: var(--text-color-primary) !important;
}
.ic-DashboardCard__header-subtitle {
    color: var(--text-color) !important;
}
.ic-DashboardCard__header-term {
    color: var(--text-color) !important;
}
/* === END Dashboard Card === */

/* === Sidebar === */
#header {
    background: var(--brand-color);
}

#header .ic-app-header__menu-list-item--active .menu-item-icon-container {
    color: var(--brand-color);
}

#header .ic-app-header__menu-list-item--active .menu-item__text {
    color: var(--brand-color);
}
/* === END Sidebar === */

.ic-Dashboard-header__layout {
    background: none;
}

/* === To-do sidebar === */
.ToDoSidebarItem {
    text-overflow: ellipsis;
    justify-content: space-around;
}
.ToDoSidebarItem__Info {
    /* 
     * Setting this item's width to 0 will make sure that it doesn't
     * take in all the space that the other items need.
     * The flexbox and the text-overflow ellips will fix this.
     */
    width: 0%;
}
.ToDoSidebarItem__Info .ToDoSidebarItem__Title a,
.ToDoSidebarItem__Icon{
    color: var(--link-color);
}
.Button.button-sidebar-wide {
    background: var(--button-color-background);
    color: var(--link-color-tertiary);
    border-color: var(--button-color-background);
}
/* the "Show all" button in the to-do sidebar */
.MYpOQ_bGBk.eHiXd_bGBk.eHiXd_brAJ.eHiXd_ycrn.eHiXd_bNlk.eHiXd_cuTS {
    color: var(--link-color);
}
/* === END To-do sidebar === */


/* ==== Navbar Icon Height ==== */
body .ic-app-header__logomark {
    height: 54px !important;
}

body.primary-nav-expanded .ic-app-header__logomark {
    height: 84px !important;
}
/* ==== END Navbar Icon Height ==== */



/*
 * COURSE PAGE
 */

/* === Course Page Buttons === */
.list-view>nav>ul>li>a:hover {
    background: hsl(0, 0%, 30%);
}
.list-view>nav>ul>li>a:focus {
    background: hsl(0, 0%, 35%);
}
.list-view>a.active {
    color: hsl(0, 0%, 100%) !important;
}
.list-view>ul>li>a, .list-view>nav>ul>li>a {
    color: var(--link-color);
}
/* === END Course Page Buttons === */

/* === Breadcrumbs === */
#breadcrumbs {
    background: transparent;
}

#breadcrumbs a {
    color: #888 !important;
}

#courseMenuToggle {
    color: #888 !important;
}

.ic-app-nav-toggle-and-crumbs {
    border-bottom-color: #aaa;
}
/* === END Breadcrumbs === */

/* === Page Content === */
.header-bar {
    background-color: transparent;
}

.course-title {
    color: #888 !important;
}

.show-content.user_content p,
.show-content.user_content span {
    color: var(--text-color) !important;
}

.show-content.user_content a {
    color: var(--link-color-tertiary);
}

.show-content.user_content .ui-icon {
    color: var(--link-color-tertiary) !important;
}

.show-content .Button {
    background: var(--button-color-background);
    border-color: var(--button-color-background);
}
/* === END Page Content === */




}
