css:
@-moz-document domain("osu.ppy.sh") {
/* Self-insert */
a[data-user-id="4428094"], a[data-user-id="4428094"]:active, a[data-user-id="4428094"]:focus, a[data-user-id="4428094"]:hover, 
a[href="/u/4428094"], a[href="/u/4428094"]:active, a[href="/u/4428094"]:focus, a[href="/u/4428094"]:hover,
.beatmap-discussion-post__user-link[href="/users/4428094"],
div[data-orig-title="KatouMegumi"] {
    color: #fdff99; /* BECAUSE ERIRI BEST GIRL. I DON'T CARE THAT MY USERNAME IS LITERALLY "KatouMegumi"!*/
}
/* Forum font */
body .osu-page--forum, body .header-v4__content, body .osu-page--forum-topic, body .sticky-header {
    font-family: "Arial Grande",Tahoma,Helvetica,Arial,sans-serif;
    font-size: 8pt;
}
/* Threads */
.osu-page--forum, .header-v4__content, .osu-page--forum-topic, .sticky-header__body, .osu-page--forum-topic-reply {
    max-width: 860px;
    box-shadow: 0 3px 4px 0 rgba(50,50,70,0.1);
}
.sticky-header__content {
    position: relative;
    max-width: 600px;
    bottom: 23px;
    margin-left: auto;
    margin-right: 8px;
    text-align: right;
}
.sticky-header__body {
    position: relative;
    left: 70px;
    height: 24px;
    padding: 0;
    padding-left: 8px;
    border-bottom: solid 1px hsl(var(--base-hue),10%,30%);
}
.sticky-header-breadcrumbs {
    margin-top: 3px;
}
.forum-post-info {
    width: auto;
    min-width: 150px;
    max-width: 230px;
}
.forum-post__content--header {
    padding: 4px;
    padding-top: 1px;
    padding-bottom: 0;
    margin-left: auto;
}
.forum-post-content {
    font-size: 12px;
}
.forum-post__actions { /*quote buttons*/
	position: relative;
 	display: block flow;
	height: 0px;
    top: -43px;
    bottom: 10px;
    right: 10px;
    margin-left: auto;
}
.forum-post__content--main {
    padding: 8px;
}
.forum-post-info__row--support-level {
	padding: 0;
    width: 80px;
    height: 12px;
    border-radius: 0px;
}
.forum-post-info__row--username, .forum-post-info__row--username:active, .forum-post-info__row--username:focus, .forum-post-info__row--username:hover {
    font-size: 8px 150%;
    max-width: 150px;
    width: 140px;
    overflow: hidden;
    text-overflow: ellipsis;
    text-align: center;
    position: absolute;
    top: -0px;
}
.forum-post-info__row--avatar {
    margin-top: 25px;
    width: 128px;
    height: 128px;
    box-shadow: 0 0 0 3px #333333, 0 0 0 4px hsl(var(--base-hue),10%,30%), 0 6px 12px rgba(0,0,0,0.15);
}
.avatar--forum {
    border-radius: 0;
    max-height: 128px;
    max-width: 128px;
}
.forum-post-info__row {
    padding: 0;
}
.forum-post-info__row--registration {
    text-align: center;
    max-width: 130px;
}
.forum-post__content--signature {
	max-height: 169px;
    min-height: 100px;
    margin-right: 0;
    margin-left: 0;
    margin-bottom: 0;
    padding: 4px;
    border-bottom: 10px solid hsl(var(--base-hue),10%,19%);
    border-top: 1px dashed hsl(var(--base-hue),10%,40%);
	background: hsl(var(--base-hue),10%,19%);
}
.forum-post-edit__body {
    min-height: 20px;
}
.forum-post__content--main {
	min-height: 148px;
}
.bbcode h4 {
    margin-top: 0;
    margin-bottom: 0;
}
.bbcode blockquote {
    padding: 3px 5px;
    margin: 1px 0 0 0;
	background: hsl(var(--base-hue),10%,24%);
	border-style: solid;
    border-color: hsl(var(--base-hue),10%,17%);
 	border-left-width: 5px;
    border-right-width: 5px;
    border-top-width: 1px;
    border-bottom-width: 1px;
}
pre { /*notice*/
    background-color: #f5f5f5;
	border: 1px solid #ccc;
	margin: 6px;
	padding: 5px;
    border-radius: 0;
}
.warning-box__icon {
	padding: 10px;
    font-size: 10px;
}

/* Forum Listing */
.header-v4__row--bar {
    padding-left: 12px;
}
.title--no-margin {
    position: relative;
    right: 26px;
}
.forum-list {
    padding-left: 36px;
    padding-right: 36px;
}
.forum-list:after {
    display: none;
}
.forum-title--forum {
    margin: 0;
    display: none;
}
.forum-item__subforums {
    margin-top: 0;
}
.forum-item {
    min-height: 45px;
    max-height: 70px;
    padding: 0px;
}
.forum-item__latest-post-label {
    height: 0;
    font-size: 0;
}
.forum-item__details {
    padding-left: 43px;
}
.fa-lock {
    display: none;
}
.forum-topic-entry {
    height: 33px;
    padding: 4px;
}
.forum-item-stripe {
    display: none;
}
.forum-topic-entry__icon {
    min-width: 24px;
    width: 24px;
    height: 24px;
    position: relative;
    left: 4px;
    font-size: 12px;
}
.forum-list__buttons {
    right: 20px;
}
.btn-osu-big {
    min-width:100px;
    padding:5px 10px 5px 10px;
    border-radius:6px;
}
}
