css:
@-moz-document url-prefix("https://www.guomoo.biz") 
{
body,.title_and_rss,body.custom-background,.title_and_rss_inner,.post_path {background: #202020;}
p {color: #cccccc;}

.wrapper_inner {max-width: 97.5%}
.wrapper_inner #content {width: 100%; display: flex; flex-wrap: wrap; max-width: 100%;}
.wrapper_inner #content .post {clear: initial;width: 20%; float: left; display: initial; margin-bottom: 50px;}
.wrapper_inner #content .post #content .post .entry {width: 100%;}
.wrapper_inner #content .post #content .post .entry p {font-size: 15px; width: 100%;}
.entry p a {font-size: 15px;}
.wrapper_inner #content .post #content .post .entry p strong a {font-size: 15px; width: 100%;}
#wrapper,.post {overflow: unset;}

.wrapper_inner #content .post #entry_font,.wrapper_inner #content .post .p_meta_s_t {width: 400%}
#comments-div {width: 100%;}

.archive_title,.post_path {width: 100%;}
#sidebar .widget #searchform #s {width: 90%; padding: 5px;}
.entry #searchsubmit, #sidebar .widget #searchsubmit {width: 7.5%; padding: 5px;}
.entry #searchform #s, #sidebar .widget #searchform #s,.entry #searchsubmit, #sidebar .widget #searchsubmit {height: 25px; font-size: 20px;}

.title, .title a {font-weight: 100; text-shadow: none; color: #cccccc; font-size: 20px;}
.title {padding-right: 50px;} 
.title a:hover {text-shadow: none; color: #8ce83b;}

a,#sidebar a,.wp-pagenavi a.first, .wp-pagenavi a.last {color: #7bda28; font-size: 15px;}

#commentform textarea {background: #505050; color: #cccccc;}

a:hover {color: #aaaaaa;}
.p_meta a {color: #cccccc;}
.p_meta a:hover,#sidebar a:hover {color: #7bda28;}
.wp-pagenavi {width: 100%}
#sidebar li {padding: 5px 0; line-height: 15px; color: #cccccc;}
.wp-pagenavi a, .wp-pagenavi span.current, .wp-pagenavi span.pages, *+html .wp-pagenavi span.pages {line-height: 50px; width: 50px; height: 50px; font-size: 20px;}
#sidebar {width: 100%; float: left;}
#recent-posts-2 ul li {width: 50%; float: left;}
#categories-2 ul li {width: 20%; float: left;}
#categories-2 ul li,.wp-pagenavi a, .wp-pagenavi span.current, .wp-pagenavi span.pages, *+html .wp-pagenavi span.pages {color: #cccccc;}
}

