css:
@document url-prefix("http://meihuafuli.blog.fc2blog.us"), url-prefix("https://meihuafuli.blog.fc2blog.us")
{
#container,div.page_navi {width: 100% !important;}
div#header {padding: 0%; margin-bottom: 0%;}
div#container {min-width: auto !important; max-width: 100% !important; float: left;}
div#header,body {background: #202020;}
div.content,div.form {background: #252525;}
p.sub_header,div.form dl dd textarea {background: #252525; margin: 0 auto;}
div#primary,dl.sidemenu_body {margin-right: 0;}
div#main,div#sidemenu {margin: auto !important; width: 97.5%; display: flex; flex-wrap: wrap;}
div.content {width: 50%; float: left;}
dl.sidemenu_body {width: 20%; float: left;}
div#sidemenu {float: left; margin: auto !important;}
div.page_navi,dl.sidemenu_body {margin: 0; padding: 0; padding-bottom: 25px;}
dl.sidemenu_body {margin: 0;}
div.entry_body {padding: 5px;}
.entry_body img > br {width: 0; height: 0;}
br.entry_body, .entry_body br {width: 0; height: 0; line-height: 0;}
.entry_body img {width: 49%; padding: 0 0.25%; float: left;}
div.form dl dd textarea {color: #cccccc;}
div.pagetop {text-align: left; padding: 10px 20px;}
}
