css:
@-moz-document url-prefix("chrome-extension://kdinfjomkigjcjcbigolloleeiianaif/edit.html") {
body
{
    background: #222;
    color: #ccc;
}

.CodeMirror-scroll, .CodeMirror, .CodeMirror-lines, .CodeMirror-code
{
    background: #333;
}

.CodeMirror-gutters, .CodeMirror-activeline, .CodeMirror-activeline-background, CodeMirror-linebackground
{
    background: #444;
}

.CodeMirror-line::selection, .CodeMirror-line > span::selection, .CodeMirror-line > span > span::selection, .CodeMirror-line, input:not([type]), select, input
{
    background: #555;
    color:#ccc !important;
}

.CodeMirror-gutters, .CodeMirror-scroll, body, .CodeMirror, input:not([type]), select, input, header, #header
{
    border: 0;
}

.cm-matchhighlight, .cm-overlay
{
    background: #444;
}

.cm-variable, CodeMirror-cursors, .cm-overlay, .cm-qualifier
{
    color:#ccc
}

.cm-s-default .cm-qualifier
{
    color: #acc16a;
}

.cm-s-default .cm-atom, .cm-s-default .cm-number
{
    color: #bd8843;
}

.cm-s-default .cm-def
{
    color: #7480bf;
}

.cm-s-default .cm-keyword
{
    color: #ac6161;
}

.cm-s-default .cm-builtin
{
    color: #65bd98;
}

.cm-s-default .cm-tag
{
    color: #629d8a;
}
}
