css:
@-moz-document domain("rodsbooks.com") {
/* http://meyerweb.com/eric/tools/css/reset/ 
   v2.0 | 20110126
   License: none (public domain)
*/

html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed, 
figure, figcaption, footer, header, hgroup, 
menu, nav, output, ruby, section, summary,
time, mark, audio, video, .tight {
	margin: 0;
	padding: 0;
	border: 0;
	font-size: 100%;
	font: inherit;
	vertical-align: baseline;
}
/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure, 
footer, header, hgroup, menu, nav, section {
	display: block;
}
body {
	line-height: 1;
}
ol, ul {
	list-style: none;
}
blockquote, q {
	quotes: none;
}
blockquote:before, blockquote:after,
q:before, q:after {
	content: '';
	content: none;
}
table {
	border-collapse: collapse;
	border-spacing: 0;
}

div[style] {
    float:none !important;
    width:auto !important;
}

img {
    padding-bottom: 2em;
    width: auto;
    height: auto;
    max-width: 100%;
}

/* -------------------------------------------------------*/

* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box
}

html {
    background:#343131;
}

body {
    font-family: "Lato","proxima-nova","Helvetica Neue",Arial,sans-serif;
    line-height: 1.2em;
    position: absolute;
    left: 400px;
    padding: 2.5em 2em 2em 2em;
    font-weight: normal;
    color: #404040;
}

a[href] {
    color: #0066ff;
    font-weight: 300;
    text-decoration: none;
}

a[href]:hover {
    text-decoration: underline;
    color: #dc8400;
}

a[href]:active {
    color: #ce0000;
}

p {
    line-height: 24px;
    margin: 0;
    font-size: 16px;
    margin-bottom: 1em
}

hr {
    border-style: solid;
    border-width: 0.5px;
    border-color: #e0e0e0;
}
h1, h2, h3, h4, h5, h6 {
    margin-top:2em;
    margin-bottom:0.75em;
    text-align:left;
}

h1 > br, h2 > br, h3 > br, h4 > br, h5 > br, h6 > br {
   display: none;
}

h1 {
    font-size: 175%
}
h2 {
    font-size: 150%
}
h3 {
    font-size: 125%
}
h4 {
    font-size: 115%
}
h5 {
    font-size: 110%
}
h6 {
    font-size: 100%
}
small {
    font-size: 80%
}
sub, sup {
    font-size: 75%;
    line-height: 0;
    position: relative;
    vertical-align: baseline
}
sup {
    top: -0.5em
}
sub {
    bottom: -0.25em
}

b, strong {
    font-weight: bold
}

blockquote {
    margin: 0
}

pre, code, samp {
    font-family: monospace, serif;
    _font-family: "courier new", monospace;
    font-size: 1em;
    white-space: pre;
}

ul {
    margin-left:2em;
    list-style-type: square;
}

ol {
    list-style-type: decimal;
    margin-left:2em;
}

ol li, ul li {
    margin-bottom:1.5em;
}

ol li:first-child, ul li:first-Child {
    margin-top:1em;
}

caption {
    background: #2980b9;
    color: #ffffff;
    padding: 0.5em 1em;
    font-size: 14px;
    text-align: left;
}
caption b {
    font-weight:100;
}

/* Page Styles */

.subhead {
    font-size:1em;
    font-family: "Lato", "proxima-nova", "Helvetica Neue", Arial, sans-serif;
    text-align:left;
}

.sidebar {
    border: 1px solid #b5b5b5;
    background: #d8d8d8;
    padding: 1em 1.5em;
    margin: 1em;
    float: none;
    font-size: 0.8em;
    width:95%
}

.userinput {
    font-weight: lighter;
    clear: both;
    display: inline-block;
    background: hsla(0, 0%, 96%, 1);
    color: #131313;
    border: 1px solid hsla(0, 0%, 85%, 1);
    padding: 0px 1em;
    margin-bottom: 1px;
}

tt {
    font-weight: 700;
    font-family: "Consolas", "Incosolata", "Consolata", "Monaco", monospace;
    font-size:100%;
    letter-spacing: 0em;
}

.listing, code {
    max-width: 100%;
    width: 100%;
    background: #191919e0;
    border: solid 1px #000000;
    font-family: "Consolas", "Incosolata", "Consolata", "Monaco", monospace;
    font-size: 12px;
    padding: 0.5em 1em;
    margin-bottom: 1em;
    color: #d2d2d2;
    overflow-x: auto;
}

.listing > .userinput {
    background:none;
    color:#a9ddff;
    border:0px solid white;
}

.highlight {
    border: 1px solid #00000042;
    background: #ffa50038;
    color: black;
    padding: 0.5em 1em;
    font-size: 14px;
}


/* Navigation */

.navbar {
    width: 400px;
    padding: 0px;
    border: 0px solid gray;
    margin: 0px;
    background-color: #343131;
    position: fixed;
    left: 0px;
    top: 0px;
    height: 100%;
    overflow: hidden;
}

.navbar h4 {
    color: #fcfcfc;
    font-size: 100%;
    font-weight: bold;
    text-align: left;
    padding: 1.4em;
    background-color: #2980b9;
    margin-top: 0px;
}

.navbar > ul > li > ul > li a {
    padding-left:4em
}

.navbar > ol li:first-child, .navbar > ul li:first-Child {
    margin-top:0em;
}

.navbar ul {
    display: block;
    margin: 0;
    padding: 0;
    padding-top:7px;
    list-style: none;
    list-style-image: none;
    margin-inline-start: 0px;
    margin-inline-end: 0px;
    margin-bottom: 0;
}

.navbar li {
    display: list-item;
    text-align: -webkit-match-parent;
    padding-top: 4px;
    padding-bottom: 4px;
    line-height: 0px;
}

.navbar > ul > li {
    color:#6c6c6c;
    font-size:0px;
}

.navbar a {
    text-decoration:none;
    line-height: 18px;
    padding: 0em 1.618em;
    display: block;
    position: relative;
    font-size: 14px;
    color: #b3b3b3;
    box-sizing: border-box;
}

.navbar a:hover {
    background-color: #4e4a4a;
    text-decoration:none;
    color:white;
    cursor: pointer;
    padding-top: 4px;
    padding-bottom: 4px;
    margin-top: -4px;
    margin-bottom: -4px;
}

.navbar a:active {
    background-color: #2980b9;
    color:white;
    cursor: pointer;
    padding-top: 4px;
    padding-bottom: 4px;
    margin-top: -4px;
    margin-bottom: -4px;
}

a[href="index.html"] {
    position: fixed;
    top: 0px;
    left: 336px;
    font-size: 0px;
    background: hsla(204, 64%, 35%, 1);
    border-left: 1px solid hsla(204, 94%, 26%, 1);
    width: 64px;
    height: 64px;
    text-align:center;
    vertical-align:middle;
    text-decoration: none;
}

a[href="index.html"]:hover {
    text-decoration:none;
    background:white;
}

a[href="index.html"]:after {
    text-decoration:none;
    font-family: "Consolas", "Incosolata", "Consolata", "Monaco", monospace;
    content:"â†";
    font-size:36px;
    color:white;
    display:block;
    position:relative;
    top:-4px;
}

a[href="index.html"]:hover:after {
    color:#4e4a4a;
}


/* Charity Box */

body > table:first-of-type {
    display: block;
    position: absolute;
    top: 0px;
    right: 0px;
    z-index: 100;
    text-transform: uppercase;
    font-size: 10px;
    color: #2980b9;
    padding: 0em 2em;
    background: #e6e6e6cf;
}

body > table:first-of-type td, body > table:first-of-type th {
    border:0px;
    padding:2px;
    text-align:center;
}


body > table:first-of-type:hover {
    opacity:1;
    filter: alpha(opacity=100);
}



body > table:first-of-type tr:nth-child(2) input[name="submit"] {
/*     background:transparent !important; */
}

/* Modification Date */

body > p:nth-child(3) {
    background: hsla(0, 0%, 90%, 1);
    color: #8a8a8a;
    position: absolute;
    top: 0px;
    font-family: "Consolas", "Incosolata", "Consolata", "Monaco", monospace;
    font-size: 11px;
    padding: 2em 3em;
    height: 64px;
    width: 100.09%;
    margin-left: -3em;
}


/* Tables */

table {
    font-size:14px;
}

th {
    background: #343131;
    color: white;
    vertical-align: middle;
}

table td, table th {
    padding:0.5em 1em;
    border: 1px solid #e6e6e6;
}

table th {
    border:1px solid hsla(0, 3%, 17%, 1);
}
}
