css:
@-moz-document domain("www.fxp.co.il") {
	body {
	background: #212324;
    color: white !important;
}

.quote_container {
	background: #212324;
}

.prefixtit {
	color: #1DA1F2; 
}

#topmenunew > div.topbluew, 
#topmenunew > div.topbluew > div,
body > div.fixedlogin > div,
.threadbit,
.dargot.gu,
.flo,
.breadcrumb,
.thread_controls,
.userinfo,
.userinfo_noavatar,
.postrow,
.textcontrols,
.popupbody,
.popupbody li a {
background: #121212 !important;
}

.popupbody li a:hover {
	color: #1DA1F2;
}

.dargot a, 
.dargot, 
.flo, 
.flo a, 
.description_clean,
.breadcrumb, .breadcrumb a,
.titleshowt h1,
.postbit .postbody,
.popupmenu.memberaction span[style="color:#000000"],
.usertitle,
.posthead-date .date,
.posthead-date .postcounter {
	color: white !important;
}

.threadbit * {
	color: white !important;
}

.threadbit, .breadcrumb {
 box-shadow: 0 0 5px rgba(0, 0, 0, .5);
}

#top-menu > li > a,
.toplogin.toplogedin a {
	color: white;
}

.textsik {
	color: white;
}

.textsik:hover {
	color: #1DA1F2;
}


.divmainfav {
	background-size: cover;
}

.divmainfav:after {
	background: rgba(0, 0, 0, .75);
    backdrop-filter: blur(3px);
}


li.cats > div > div, .ftitle.bluegr {
	border: none !important;
}

li.cats {
	background: #121212;
    border: none;
    box-shadow: 0 0 5px rgba(0, 0, 0, .3);
    transition: .3s;
    backface-visibility: hidden;
}

li.cats:hover {
	transform: translateY(-4px);
}



.forumbit_post.L2 a {
	color:  white;
} 

.forumbit_post.L2 {
	transition: .3s;
}

.forumbit_post.L2:hover {
	transform: translateX(-3px);
}

.forumbit_post.L2:hover a {
	color: #1DA1F2;
}

[class^="ob"], [data-google-query-id] {
	display: none !important;
}

body > div.divhed > div:nth-child(12) h1 {
	color: white;
}


.pagetitle.background-gif {
	position: relative;
}

.pagetitle.background-gif:after {
	content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, .7);
    backdrop-filter: blur(3px);
}

.ntitle {
	position: relative;
    z-index: 1;
}
}
