css:
@-moz-document domain("community.adobe.com") {
/*Header and Footer*/
.spectrum .adobe-global-nav,.spectrum .adobe-global-footer {
    background-color: #000;
    border:none
}

/* dark mode page background */
.spectrum--lightest,.lia-message-search-container>.lia-quilt>.lia-quilt-row-main, .lia-search-container>.lia-quilt>.lia-quilt-row-main,.PostPage .lia-quilt-row-sub-header,.PostPage .lia-breadcrumb, .ReplyPage .lia-breadcrumb, .EditPage .lia-breadcrumb, .CrmSupportCasePage .lia-breadcrumb,.lia-top-quilt>.lia-quilt-row-main>.lia-quilt-column-left-content>.lia-quilt-column-alley, .lia-top-quilt>.lia-quilt-row-main>.lia-quilt-column-right-content>.lia-quilt-column-alley, .lia-top-quilt>.lia-quilt-row-main>.lia-quilt-column-single>.lia-quilt-column-alley,.lia-message-editor {
    background-color: #111;
}

.banner-container
{
visibility:hidden;
height:50px!important
}
.banner-container:before
{
visibility:visible;
content:"Adobe Support Community";
font-size:48px;
font-weight:800
}

/* serif font fix */
.ForumPage .preview-card__serif-font-family,html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, .lia-fieldset-title, .lia-component-tkb-article-editor-form .lia-form-compare-title, .lia-component-tkb-article-editor-form .lia-revision-info-title, .lia-component-tkb-article-editor-form .lia-related-messages-title, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video,.spectrum.ForumTopicPage .lia-linear-display-message-view .MessageView .lia-message-body-content {
    font-family: "adobe clean",sans-serif
}

/* dark mode text colors*/

a, a:hover
{
    color:#5af
}

.spectrum--lightest .spectrum-Link,.spectrum .lia-media-snippet-container a, .spectrum .lia-message-body a, .spectrum .lia-message-body-content a, .spectrum .lia-message-editor a
{
    color:#5af
}

.ForumPage .preview-card__subject a, .spectrum.ForumTopicPage .lia-linear-display-message-view .MessageView .lia-message-subject,.comm-card .comm-card__link,.home-wrapper .banner-container .banner-content .heading,#lia-body.SearchPage .lia-search-results .lia-search-results-container.lia-message-search-results-list-container .MessageView.lia-message-view-message-search-item.lia-message-view-display .lia-message-subject-wrapper.lia-component-subject span a,#lia-body.SearchPage .lia-search-results .search-result-count,.lia-tabs-standard li>span>a, .lia-tabs-standard .lia-tab-overflow .default-menu-option, .lia-tabs-standard .lia-tabs-active>span>a, .lia-tabs-standard>.lia-tabs-active .default-menu-option,.spectrum--lightest .spectrum-Heading6,.ViewProfilePage .lia-page-header .lia-component-common-widget-page-title .lia-link-navigation,.lia-content input[type=text], .lia-content select, .lia-content textarea, .lia-form-subject-input, .lia-form-tiny-mce-editor-input, .lia-form-type-text, .lia-message-editor, .mce-edit-area.mce-panel, .mce-form .mce-textbox, .lia-form-input-wrapper .lia-token-input-list {
    color: #ddd
}

.spectrum .translate-component__container .translate-button__container .u-tooltip-showOnHover .text-btn__container .translate-button__text, .spectrum .community-header, .spectrum .community-topics__container .community-topics__heading .community-topics__heading--left .spectrum-Body1.community-topics__heading_text,.spectrum .spectrum-Body1.related-communities__heading,.ForumPage .spectrum-Body4.preview-card__user, .ForumPage .preview-card__user.lia-component-forums-action-move-checked, .ForumPage .preview-card__user.lia-component-forums-action-delete-checked-threads, .ForumPage .spectrum #admin-control .preview-card__user.lia-component-forums-action-delete-thread, .spectrum #admin-control .ForumPage .preview-card__user.lia-component-forums-action-delete-thread, .ForumPage .spectrum #admin-control .preview-card__user.lia-component-forums-action-mark-thread-read-only, .spectrum #admin-control .ForumPage .preview-card__user.lia-component-forums-action-mark-thread-read-only, .ForumPage .spectrum #admin-control .preview-card__user.lia-component-forums-action-move-message, .spectrum #admin-control .ForumPage .preview-card__user.lia-component-forums-action-move-message, .ForumPage .spectrum #admin-control .preview-card__user.lia-component-subscriptions-action-add-thread-global-featured, .spectrum #admin-control .ForumPage .preview-card__user.lia-component-subscriptions-action-add-thread-global-featured, .ForumPage .spectrum #admin-control .preview-card__user.lia-component-subscriptions-action-add-thread-user-email, .spectrum #admin-control .ForumPage .preview-card__user.lia-component-subscriptions-action-add-thread-user-email, .ForumPage .spectrum #admin-control .preview-card__user.lia-component-forums-action-unmark-thread-read-only, .spectrum #admin-control .ForumPage .preview-card__user.lia-component-forums-action-unmark-thread-read-only, .ForumPage .spectrum #admin-control .preview-card__user.lia-component-subscriptions-action-remove-thread-global-featured, .spectrum #admin-control .ForumPage .preview-card__user.lia-component-subscriptions-action-remove-thread-global-featured, .ForumPage .spectrum #admin-control .preview-card__user.lia-component-subscriptions-action-remove-thread-user-email, .spectrum #admin-control .ForumPage .preview-card__user.lia-component-subscriptions-action-remove-thread-user-email,.spectrum .related-conversations .spectrum-Body1.related-conversations__heading,.spectrum.ForumTopicPage .lia-linear-display-message-view .MessageView .lia-message-body-content,.spectrum.ForumTopicPage .lia-linear-display-message-view .MessageView .test-wrapper .report__text,.spectrum.ForumTopicPage .lia-linear-display-message-view .MessageView .test-wrapper .follow__text a,.spectrum .related-conversations .related-conversations-card .related-conversations-card__title a, .spectrum .lia-content .reply-count__text,.spectrum.ForumTopicPage .lia-linear-display-message-view .MessageView .spectrumizedUserStrip .userStrip .userStrip__name,.spectrum .test-wrapper .kudos-btn__like-count-text,.spectrum .adobe-global-nav__breadcrumb .lia-breadcrumb>ul>:nth-child(3)>a, .spectrum .spectrum-Checkbox-label.checkbox__label-color,.home-wrapper .banner-container .banner-content .description,#lia-body.SearchPage .lia-search-results .lia-search-results-container.lia-message-search-results-list-container .MessageView.lia-message-view-message-search-item.lia-message-view-display.lia-list-row-thread-solved .message-solution-stats .message-solution-author div a,.spectrum--lightest,.spectrum .related-communities__cards .related-communities-card__container .related-communities-card .spectrum-grid-row.related-communities-card__text-container .spectrum-Body1.related-communities-card__board,.spectrum .community-header-label,.spectrum--lightest .spectrum-Body4, .spectrum--lightest .ForumPage .lia-component-forums-action-move-checked, .ForumPage .spectrum--lightest .lia-component-forums-action-move-checked, .spectrum--lightest .ForumPage .lia-component-forums-action-delete-checked-threads, .ForumPage .spectrum--lightest .lia-component-forums-action-delete-checked-threads, .spectrum--lightest .spectrum #admin-control .lia-component-forums-action-delete-thread, .spectrum #admin-control .spectrum--lightest .lia-component-forums-action-delete-thread, .spectrum--lightest .spectrum #admin-control .lia-component-forums-action-mark-thread-read-only, .spectrum #admin-control .spectrum--lightest .lia-component-forums-action-mark-thread-read-only, .spectrum--lightest .spectrum #admin-control .lia-component-forums-action-move-message, .spectrum #admin-control .spectrum--lightest .lia-component-forums-action-move-message, .spectrum--lightest .spectrum #admin-control .lia-component-subscriptions-action-add-thread-global-featured, .spectrum #admin-control .spectrum--lightest .lia-component-subscriptions-action-add-thread-global-featured, .spectrum--lightest .spectrum #admin-control .lia-component-subscriptions-action-add-thread-user-email, .spectrum #admin-control .spectrum--lightest .lia-component-subscriptions-action-add-thread-user-email, .spectrum--lightest .spectrum #admin-control .lia-component-forums-action-unmark-thread-read-only, .spectrum #admin-control .spectrum--lightest .lia-component-forums-action-unmark-thread-read-only, .spectrum--lightest .spectrum #admin-control .lia-component-subscriptions-action-remove-thread-global-featured, .spectrum #admin-control .spectrum--lightest .lia-component-subscriptions-action-remove-thread-global-featured, .spectrum--lightest .spectrum #admin-control .lia-component-subscriptions-action-remove-thread-user-email, .spectrum #admin-control .spectrum--lightest .lia-component-subscriptions-action-remove-thread-user-email,.spectrum--lightest .spectrum-Button--primary.spectrum-Button--quiet,.lia-component-common-widget-page-title>.lia-link-navigation, .lia-discussion-page-header-subject, .lia-qanda-question-page-subject, .lia-thread-subject, .lia-message-blog .blog-article-link, .lia-messages-search-before-post-title,.spectrum--lightest .spectrum-Heading2--quiet,.lia-message-editor-preview-button,.lia-button-Cancel-action.lia-button-Cancel-action,.lia-attachments-drop-zone .lia-attachment-description-text
{
    color: #aaa
}

.ForumPage .preview-card__subject a:hover {
    color: #5af;
}

/*Forum topic list*/
.comm-card,.lia-message-view-display,#lia-body.SearchPage .lia-search-results .lia-search-results-container.lia-message-search-results-list-container .MessageView.lia-message-view-message-search-item.lia-message-view-display,.SearchPage .lia-tabs-standard-wrapper>.lia-tabs-standard,.spectrum.ForumTopicPage .lia-component-reply-list .level2.lia-linear-display-message-view,.lia-content input[type=text]
{
    background:#2220;
    border:none;
    border-bottom: 1px dotted
}

.lia-truncate-with-message-link:after
{
    background:#1110
}
.lia-truncate-with-message-link .lia-truncated-message-link {
    background-color: #111d;
}
    
    
.comm-card:nth-child(odd)
{
    background:#1a1a1a!important
}

.preview-card__translate-container:nth-child(odd)
{
    background:#1a1a1a;
}

.preview-card__translate-container
{
    border-top:1px dotted #555
}
.ForumPage .preview-card__no-solution-border
{
    border:none
}
.spectrum--lightest .spectrum-FieldButton {
    color: #aaa;
    background-color: #222;
    border-color: #aaa;
}

.spectrum .product-feedback__button{
    color:#ddd
}

.spectrum .product-feedback__button img
{
    filter:invert(90%)
}

/*Conversations List*/
.ForumPage .spectrum-Body3.preview-card__body,.ForumPage .preview-card__no-solution-subject-padding,.ForumPage .preview-card__timestamp-margin,.ForumPage .preview-card__post-stats-strip-image-stat-alignment,.preview-card__solution-subject-padding
{
    font-family:"adobe clean",sans-serif;
    padding-left:10px;
    padding-right:10px
}

/*search interface dark mode*/
#lia-body .spectrum-grid--fluid.adobe-global-nav .lia-component-common-widget-search-form .SearchForm.lia-form-inline .lia-search-input-wrapper .search-input.lia-search-input-message, #lia-body.CommunityPage .spectrum-grid--fluid.adobe-global-nav .lia-component-common-widget-search-form .SearchForm.lia-form-inline .lia-search-input-wrapper .search-input.lia-search-input-message
{
    background:#333;
    color:#ddd
}

.lia-form-type-text,.lia-form-type-search:active,.lia-form-type-search:focus
{
    background:#111
}

/* Reply Indent */
.spectrum.ForumTopicPage .lia-component-reply-list {
    background-color: #111

}

.spectrum.ForumTopicPage .lia-component-reply-list .lia-linear-display-message-view .custom-reply-indent-2 {
    background-color: #111;
    padding-left:30px
}

.spectrum.ForumTopicPage .lia-component-reply-list .level3.lia-linear-display-message-view
{background:#111}

.lia-message-view-wrapper
{
    border-bottom:1px dotted #555
}

.lia-message-body-content span
{
    background:#222 !important;
    color:#aaa !important
}

.spectrum--lightest .spectrum-Heading3 {
    color: #aaa;
}
.spectrum.ForumTopicPage .lia-component-reply-list .lia-linear-display-message-view .custom-reply-indent-3 {
    padding-left: 20px;
    background-color: #151515;
}

.spectrum.ForumTopicPage .lia-component-reply-list .lia-linear-display-message-view .custom-reply-indent-3 .MessageView {
    border-left: 1px dotted #555;
    background-color: transparent;
    padding-left: 16px;
}

.message-list:nth-child(even)
{
    background:#1a1a1a!important
}

.lia-panel-message
{
    padding-left:15px;
padding-right:15px
}

/* compose reply hides banner image recolors UI */
.spectrum.ForumTopicPage .lia-linear-display-message-view .MessageView .editor-banner .editor-banner__container .editor-banner__image
{
    visibility:hidden
}
.spectrum.ForumTopicPage .lia-linear-display-message-view .MessageView .editor-banner
{
    background:#111;
    border: 1px solid;
    border-radius:10px;
    height:150px
}
.spectrum.ForumTopicPage .lia-linear-display-message-view .MessageView .editor-banner .editor-banner__container .editor-banner__heading
{
    color:#ddd
}
.spectrum.ForumTopicPage .lia-linear-display-message-view .MessageView .editor-banner .editor-banner__container .learn-more__text
{color:#5af}

.spectrum .mce-container-body.mce-stack-layout .mce-top-part.mce-container.mce-stack-layout-item.mce-first {
    border: 1px solid #aaa;
    border-radius: 5px 5px 0px 0px;
    background-color: #222;
}

.spectrum .mce-container-body.mce-stack-layout .mce-edit-area.mce-container.mce-panel.mce-stack-layout-item {
    border: 1px solid #555;
    border-radius: 0px 0px 5px 5px;
    border-top-style: none;
    background-color: #333;
}

/*related communities cards */
.spectrum .related-communities__cards .related-communities-card__container .related-communities-card {
    padding: 14px;
    height: 130px;
    border: 1px solid #555;
    background-color: #111;
    border-radius: 10px;
    margin-bottom: 10px;
}

/* tags */
.spectrum .community-topics__container .community-topic__list_container .spectrum-Tags-item {
    background-color: #111;
    border: 1px solid #555;
    color: #aaa
}

/* Post Page */
.PostPage .editor-banner__image
{
    filter:invert(100%)saturate(10%)
}

.PostPage .post-page-guidelines
{
    background:#222
}

.PostPage .lia-content input[type="text"] {
    height: 32px;
    color: #ddd;
    border: 1px solid #555;
    border-radius: 4px;
    box-shadow: none;
}

[style*="color: #333333"]
{
color:#28c !important
}

.lia-quilt-row-sub-header
{
    border:none
}

.lia-top-quilt>.lia-quilt-row-footer
{
    background:#0000;
    border:1px dotted #555
}

.PostPage .spectrum-Tags-item
{
    color:#ddd;
    background:#333;
    border-color:#aaa
}

.spectrum .mce-container-body.mce-stack-layout .mce-edit-area.mce-container.mce-panel.mce-stack-layout-item {
    border: 1px solid #ddd;
    border-radius: 0px 0px 10px 10px;
    border-top-style: none;
}

.mce-panel.mce-panel
{
    background:#0000
}

.spectrum .mce-container-body.mce-stack-layout .mce-top-part.mce-container.mce-stack-layout-item.mce-first {
    border: 1px solid #ddd;
    border-top-left-radius: 10px;
    border-top-right-radius:10px
}

/* correct answer */
.correct-answer-div .message-body,.ForumPage .community-page-reply-desktop-view .community-page-reply-content
{
    background: #001100;
    border-radius: 10px;
    border:1px #00bb00 solid
}
.correct-answer-div .spectrum-Tooltip-tip,.ForumPage .community-page-reply-desktop-view .community-page-reply-content .spectrum-Tooltip-tip {
    border-width: 8px;
    border-top-color: #0b0;
}
.correct-answer-div .message-body .content,.ForumPage .community-page-reply-desktop-view .community-page-reply-content .preview-card-profile-strip-desktop-view .preview-card__user
{
    color:#ddd
}
.spectrumizedUserStrip .userStrip .userStrip__correct-answer-text, span.userStrip__name.spectrum-Body4,.lia-message-body-content span
{
   background:#0000 !important
}

.ForumPage .community-page-reply-desktop-view .community-page-reply-content
{
    margin-right:50px!important
}

/*My Settings*/

.lia-quilt-view-profile-hero {
    background-color: #222;
    border-radius:10px
}
    
.MyProfilePage .UserMacros {
    background-color: #111;
    padding: 1rem;
    border: 1px dotted #aaa;
    border-radius: 4px;
}
.lia-quilt-column-side-content .lia-panel {
    background-color: #222;
    border: 1px solid #555
}
.lia-panel .lia-panel-heading-bar
{
    background:#111
}

/*User Stats Page*/

.lia-user-badge-list
{
    background:#222;
    border-radius:10px
}
.lia-component-quilt-user-profile-statistics
{
    background:#111;
}
.spectrum--lightest .spectrum-Body3 {
    color: #ddd ;
}

.ViewProfilePage .default-menu-option {
    background-color: #111;
    font-weight: normal;
    border-color: #555;
    color: #ddd;
    font-style: italic;
    text-decoration: none;
}

.ViewProfilePage .lia-user-name-link {
    color: #5af !important;
}
}
