css:
@-moz-document domain("iqrpg.com") {
.activeText,
.new-message,
a:hover
{
    color: #e600ff !important;
}

.chat-msg-global,
.event-text
{
    color: hotpink !important;
}

a, a:visited {
    color: #b85dce;
    text-decoration: none;
    cursor: pointer;
}

.chat-msg-pm-from[data-v-6e1e5342] {
    color: #e165ff;
}

.chat-msg-pm-to[data-v-6e1e5342] {
    color: #e165ff;
}

.chat[data-v-58b59606]{
    height: 40vh;
}

.header[data-v-1a67c09d],
.header[data-v-eeed284e]{
    display: none;
}

.progress-bar-green {
    background-image: linear-gradient(#febaff,#50044e);
}

table[data-v-16b72a97]:first-of-type {
    width: 50%;
}

.table-invisible {
    width: 100% !important;
}
table.table-invisible th:not(:last-child),
table.table-invisible td:not(:last-child){
    padding-right: 0;
}
table.table-invisible th:last-child { 
    width: 30%;
}

table.table-invisible th:not(:last-child) {
    width: 17.5%;
}

table.table-invisible:last-of-type tr:nth-of-type(2) td:last-child {
    width: 30% !important;
}
table.table-invisible:last-of-type tr {
    text-align: center;
    height: 20px;    
}

tr[data-v-32a6d878]{
    text-align: center;
    height: 20px;
}

.game {
    color: #e495d6;
    max-width: inherit;
    height: 55vh;
    overflow: auto;
}

.market-form[data-v-16b72a97]{
    justify-content: center;
}

.green-text {
    color: #e495d6;
}
}
