css:
@-moz-document url("http://losergogogo.com/"), url-prefix("http://losergogogo.com"), domain("losergogogo.com") {
#recent-pjax-container > div:nth-child(1) > div > ul li,
.module-header,
.list > ul > li,
.tabs-sub,
.sub-head{
    border-bottom: none;
}

/* 主框架 */
.nav {
    background-color: #252a35;
}
.main{
    padding:32px 0 0 0;
}
a.current {
    background-color: #ea4e3f;
}

.nav .nav-tabs > a,
.nav .user-nav > span {
    padding: 14px 20px
}

.nav .nav-tabs svg {
    margin-right: 10px;
}

.index__bottom{
    padding:20px;
}

/*顶置相关（添加js）*/
.stick > li,
div.index-tabs-panels.list.article-list > ul > li{
    margin-bottom: 16px;
}
.stick > li:nth-child(1),
div.index-tabs-panels.list.article-list > ul > li:nth-child(1){
    background-color: #fffafa;
    box-shadow: inset 0px 0px 1px 1px #ff000012;
}
.stick > li:nth-child(2),
div.index-tabs-panels.list.article-list > ul > li:nth-child(2){
    background-color: #f8fcff;

    box-shadow: inset 0px 0px 1px 1px #81c2f112;
}
.stick > li:nth-child(3) {
    background-color: #f8fff9;
    box-shadow: inset 0px 0px 1px 1px #0dad6312;
}



.article-list ul > li {
    padding: 30px;
    background-color: #fff;
    transition: all 0.2s;
}
.article-list ul > li:hover {
    background-color: #f6f6f6;
}

.article-list ul > li,
.perfect-panel,
.list {
    border-radius: 10px;
}

.ft-a-title {
    color: #0e0e0e73;
}

.pagination{
    margin: 16px 0;
    border-top: 0;
    border-radius: 10px;
        box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.09);
}

.tabs-sub{
    padding:20px;
    border-radius: 10px;
    box-shadow: inset 0 0 1px 0 #bbbbbb;
    background-color: #fbfbfb;
    margin-bottom:20px;
}

/* 阅读 */

.article .article-header{
    box-shadow:0px 10px 20px rgba(0, 0, 0, 0.09);
}
.article .article-info {
    padding: 16px;
    margin: 16px 0;
    border:1px solid antiquewhite;
    background-color: #fff7ec;
    border-radius: 10px;
}
.article h1.article-title {
    text-align: left;
    font-weight: bold;
    padding: 32px 0;
}

.article .article-content {
    padding-top: 0;
}

h2.fn-ellipsis,
.article-list ul > li > h2 {
    margin: 18px 0;
}
.tabs-sub {
    padding-bottom: 20px;
}

div.module-header.fn-clear > span {
    color: #0000;
    text-shadow: none;
    padding: 16px;
    display: flex;
    justify-content: flex-end;
}

div.module-header.fn-clear > span > a {
    text-decoration: none;
    border-radius: 10px;
    padding: 8px;
    color: #b7b7b7;
    transition: all 0.2s;
}
div.module-header.fn-clear > span > a:hover {
    background-color: #ffb4672e;
}

div.module-header.fn-clear > span > a.ft-gray {
    background-color: #ffaa00;
    border-radius: 10px;
    padding: 8px;
    color: rgba(255, 255, 255, 0.86);
}
#articleCommentsPanel > div.module.nice > div.module-header{
    margin-bottom:20px;
}
#articleCommentsPanel > div.module.nice > div.module-panel.list.comments,
#comments > div.comments-header.module-header{
    padding: 10px;
    border-radius: 10px;
    background-color: whitesmoke;
  
}

.module-header {
    padding: 0;
}


.index-tabs,
.metro-border{
    display:none;
}

#comments > div.list > ul > li:first-child{
    margin-top:30px;
}

#comments > div.list > ul > li:hover{
    background-color: whitesmoke;
    border-radius: 10px;
    transition:all .4s;
}

.content-reset img{
    border-radius:10px;
}


/*贴子*/
.article .main{
    background-color:#0000;
}

.article .main .wrapper{
    padding: 30px;
    border-radius: 10px;
    border:1px solid #f4f4f4;
    background-color: #fbfbfb;
}

.comments .comment>p>img:last-child{
    margin:10px 0;
    transition:all .4s;
}

#comments > div.list > ul{
    padding:0;
}

/* 目录 */
.article #articleToC {
    border-left: 1px solid #fff7ec;
    width:18%;
    background-color:#fffdfa;
}
.article #articleToC .article-toc li.current a {
    border-radius: 10px;
    border-left:none;
    background-color: #fff7ec;
    color: #ffa324;
}
.article-toc{
    padding:30px;
}
.article-toc li{
    padding:6px;
}
/* 侧边 */
.side > .module{
    border-radius:10px;
    padding:16px;
}
/* footer */
.index .metro-item .preview{
    border-radius:10px;
    width: 80%;;
}
.index .metro-item .preview>b{
    margin-top: 28px;
}
index__bottom {
    padding:48px 0;
}
.index .metro-item .preview:hover>img{
    border-radius:0px;
    transform:scale(1.1,1.1);
}

.index .perfect-panel{
    padding: 16px 0;
    background-color:#f5fbf4;
}
.index .perfect-panel:hover{

    box-shadow:inset 0 0 0 2px #e2eee0;
    transition:all .4s;
}

.perfect-panel .count{
    font-weight:normal;
    background-color: #9fca98;
}
.count{
    color:#218610;
}

div.perfect-panel.list > ul > li{
    padding:15px;
    border-radius:6px;
    transition:all .2s;
}
div.perfect-panel.list > ul > li:hover{
    background-color:#d8f0d4;
    box-shadow:0 0 0px 1px #c7eedf;
}

.more{
    background-color: #f8fcff;
    padding: 20px;
    border-radius: 10px;
    color: #3f7bb1;
    border: 1px solid;
}
.more:hover{
    background-color: #e0f0ff;
}

/*发帖按钮*/
.radio-btn{
    display: flex;
    align-items: center;
    justify-content: center;
    bottom:48px;
    right:48px;
    box-shadow:0 0 4px rgba(245, 53, 53, 0.14), 0 4px 8px rgba(255, 0, 0, 0.28);
    transition:all .2s;
    
}
.radio-btn:hover{
    width:68px;
    height:68px;
}


/* 节点 */
#tag-pjax-container > div:nth-child(1){
    background-color: #f7f9fd;
    padding:15px 30px;
    border-radius: 10px;
    border: 1px solid #e6eefb;
}
.tag-desc>li>a{
    padding: 10px 14px;
    border-radius:10px;
    background-color:#dbe7ff;
    color:#87b0ff;
    transition:all .4s;
}

.tag-desc>li>a:hover{
    box-shadow:none;
    border-radius:10px;
    color:#699cff;
}
#tag-pjax-container > div:nth-child(1) > ul > li> div,
body > div.main > div > div.side > div:nth-child(5) > div.module-panel > ul > li > div,
.tag-desc>li>div
{
    box-shadow:0px 1px 20px rgba(0,0,0,0.2);
    top: 50px;
    border-radius:10px;
}
/*排行*/
.list>ul>li{
    padding:30px;
}
div.cmts.tooltipped.tooltipped-w > a{
    padding:10px;
    border-radius:10px;
    background-color:#699cff;
    color:white;
}
div.side > div.module.person-info > div.module-panel.tooltipped.tooltipped-s > div{
   padding: 0 24px; 
}

/*广告*/
body > div.main > div > div.side > div:nth-child(2) > div.module-header{
    padding-bottom:10px;
}

.module-panel.ad {
    padding:10px;
    border-radius: 10px;
}
.module-panel.ad > img{
    border-radius:10px;
}

/*清风明月*/
body > div.main > div > div.side > div.person-info + div.module + div.module{
    background-color:#eff7f7;
    border:1px solid #bbd0d0;
    
}

body > div.main > div > div.side > div.person-info + div.module + div.module  li:nth-child(2n){
  background-color:#deecec;
    border-radius:10px;
}
#breezemoonInput{
    line-height:32px;
    border: 1px solid rgb(209, 222, 222);
    background-color: #feffff;
    border-radius: 6px;
    box-shadow: 0 0px 8px rgba(0, 165, 165, 0) inset;
    padding: 8px 12px;
    width: 100%;
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
}
.breezemoon__input{
    padding-right: 72px !important;
}
.form input[type=text]:focus, .form input[type=password]:focus, .form textarea:focus{
    
}

/*分享*/
.article .share{
    padding: 14px;
    border-radius: 10px;
    background-color: #f7f7f7;
    transition:all .4s;
}
.article .share:hover{
    transform:scale(1.1,1.1);
}

/*个人*/
.home-invisible {
    padding: 50px 0;
    color: #757575;
    background-color: #fbfbfb;
    box-shadow:inset 0 0 1px 0 #bbbbbb;
    border-radius: 10px;
}

/*发帖*/
.article .editor-panel .wrapper{
    padding:40px 20%;
    height: 64vh;
    background-color:#f5f5f5;
}
.editor-toolbar {
    border-radius: 10px 10px 0 0;
    padding: 10px;
}
.cm-s-default.CodeMirror {
    border-radius: 0 0 10px 10px;
}

.editor-toolbar a.active, .editor-toolbar a.focus, .editor-toolbar a, .editor-toolbar label.active, .editor-toolbar label {
    transition:all .4s;
    border-radius:10px;
    margin-right:10px;
}
body > div.editor-panel > div.wrapper > div > div.fn-flex{
    margin-bottom:20px;
}
body > div.editor-panel > div.wrapper > div > div.article-comment-content > div.CodeMirror.cm-s-default.CodeMirror-wrap > div.editor-preview.content-reset.editor-preview-active{
    box-shadow: inset 0 0 10px 0 #e6e6e6;
}

.article .cm-s-default.CodeMirror{
    height:40vh;
}
.CodeMirror-scroll{
    height:38vh;
}
/*编辑器*/
body > div.main.post > div > div.post-article-content > div.CodeMirror.cm-s-default.CodeMirror-wrap{
    min-height:50%;
    height:80vh!important;
}
body > div.main.post > div > div.fn-clear.wrapper{
    padding:30px;
}

.main__down{
    background-color: rgb(255, 255, 255);
    padding: 40px 0;
}
.footer{
    background-color:#252a35;
    z-index:100;
}
}
