css:
@-moz-document domain("stackoverflow.com"), domain("superuser.com"), domain("serverfault.com"), domain("stackexchange.com") {
#hot-network-questions, .mb16.s-anchors__default.s-anchors.s-sidebarwidget__yellow.s-sidebarwidget, li:nth-of-type(3) > .nav-links, #post-form, #footer, .bottom-notice, #feed-link, #left-sidebar {
    display: none;
}

/* That stupid „Welcome Back“ header */
#overlay-header {
    display: none ! important
}

/* That useless yellow blog sidebar */
.s-sidebarwidget__yellow {
    display: none 
}

/* User badge */
.ai-start.jc-end.fw-wrap.gsy.gs8.grid.pt4.mt16 > .grid--cell.owner.post-signature > .user-info {
    display: none;
}

/* Answerer badge */
.user-hover.user-info {
    display: none;
}

/* Navigation */
li.sm\:d-none.grid--cell:nth-of-type(2) > .js-gps-track.-marketing-link,
li.sm\:d-none.grid--cell:nth-of-type(3) > .js-gps-track.-marketing-link,
.js-products-menu.js-gps-track.-marketing-link{
    display: none;
}

/* Chat notification */
.ai-center.grid.s-sidebarwidget--header, .module.mb16.s-sidebarwidget {
    display: none;    
}

/* Sign up */
.btn-topbar-primary.py8.s-btn__primary.s-btn.login-link {
    display: none;    
}

/* Question statistics */
.bc-black-2.bb.mb16.pb8.fw-wrap.grid {
    display: none;    
}

/* Question footer */
.ai-start.jc-end.fw-wrap.gsy.gs8.grid.pt4.mt16 {
    display: none;
}

/* „Ask Question“ button */
.s-btn__primary.s-btn.ws-nowrap {
    display: none;
}
}
