css:
@-moz-document domain("dashboard.aptible.com") {
.layout-sidebar .sidebar-nav ul.stack-list li.sidebar-stack-title a {
  color: #000;
}

.layout-sidebar .sidebar-nav li.title {
    color: #000;
}

a.ember-view {
  color: #000;
}

.page-header__nav-tab.active .page-header__nav-tab-link {
    color: #000;
}

.page-header__nav-tab .page-header__nav-tab-link {
    color: #444;
}

.layout-sidebar .sidebar-nav li>a, .layout-sidebar .sidebar-nav li>span {
    margin-right: 10px;
    border-radius: 0 4px 4px 0;
    outline: 0;
    font-size: 13px;
    padding: 8px 10px 8px 20px;
    color: #3d4143;
    transition-property: color,background-color;
    transition-timing-function: ease-in-out;
    transition-duration: .25s;
    border-left: 4px solid transparent;
}

.page-header__metadata .resource-metadata-item .resource-metadata-title {
    color: #3d4143;
    font-size: 11px;
    font-weight: 500;
    margin: 0 0 7px;
}

.page-header__metadata .resource-metadata-item .resource-metadata-value {
    color: #3d4143;
}

.resource-list-item .nav-panel-tabs .danger, .resource-list-item .nav-panel-tabs .success, .resource-list-item .panel-body .danger, .resource-list-item .panel-body .success, .resource-list-item .resource-metadata-title, .resource-list-item .resource-metadata-value {
    font-size: 12px;
    color: #444;
}

.cost-estimate .rate {
    font-size: 12px;
    color: #0f1896;
    font-weight: 400;
    margin: 0;
}

.service-command {
    color: #662a94;
    font-weight: 400;
    display: inline-block;
    margin: 0 0 0 10px;
}

.resource-row__meta {
    color: #662a94;
    font-size: 12px;
}

.main-wrapper .layout-sidebar {
    order: -1;
    flex: 0 0 276px;
    background: #F4F7F9;
}
}
