css:
@-moz-document url-prefix("https://www.pluginboutique.com/") {
/* site wide */

html {

    color: #9999;

}
body {

    background: #1f1f1f;
    font-family: "Roboto", Arial, sans-serif;
    font-weight: 400;
    font-size: 14px;
    padding: 0;

}
.page-deals .deal {

    position: relative;
    padding: 20px 0;
    margin: 0;
    border-top: 0px solid #383838;

}
.page-deals-sorting li {
    border-left: 1px #d8d8d8;
    padding: 0 40px;
    display: inline-block;
}
.page-category-sorting li, .page-search-sorting li {
    border-left: 1px #d8d8d8;
    padding: 0 40px;
    display: inline-block;
}
.deal-about {
    font-family: "Roboto", Arial, sans-serif;
    font-weight: 400;
    font-size: 18px;
    color: #9999;
    margin-bottom: 5px;
}
.bigtitle span, .pagetitle span, .lined span, .bigtitle a, .pagetitle a, .lined a {

    padding: 0 10px;
    background: #999;
    color: #2b2b29;
    position: relative;
    top: -16px;

}
.promobox {

    background: #141414;
    overflow: hidden;

}
.searchfilter li label {

    font-family: "Roboto", Arial, sans-serif;
    font-weight: 900;
    font-size: 14px;
    margin: 0 0 0 10px;
    cursor: pointer;
    background: #c4c3c5;
    text-align: center;
    display: block;
    width: 70px;
    height: 35px;
    vertical-align: middle;
    line-height: 35px;
    text-transform: uppercase;
    color: black;

}
.footer {

    background: #141414;
    padding: 35px 15px;

}
.footer-block {
    display: table-cell;
    width: 33%;
    padding: 20px 10px;
    border-left: 1px #7F7F7F;
    vertical-align: top;
    min-width: 200px;
}
/* my pro */
.userproduct-serial-value {

    background-color: #1f1f1f;
    padding: 15px;
    overflow: auto;

}
.userproduct-download-versions .lform .select select {
    
	color: #9999;
    height: 20px;
    background-color: #1f1f1f;
    padding-left: 15px;

}
.lform .select::after {
    content: ' ';
    position: absolute;
    right: 0;
    top: 0;
    display: block;
    width: 40px;
    height: 40px;
    background: #d8d8d8 url(https://cdn.pluginboutique.com/assets/icon/white/arrow-down_20x10-e8f142f0862bde77f89f59ce8c23a06e.png) no-repeat 50% 16px;
        background-color: rgb(216, 216, 216);
        background-position-x: 50%;
        background-position-y: 16px;
    border-left: 1px solid #fff;
    pointer-events: none;
	opacity: -1;
}
.userproduct {
    position: relative;
    border-top: 0px solid #9b9b9b;
    padding: 25px 0;
}
.walletinfo {
    display: table;
    border: 0px solid #c4c3c5;
	border-bottom: 0px solid #c4c3c5;
    margin: 0 auto;
    padding: 20px;
}
.walletinfo-section {
    display: table-cell;
    width: 230px;
    border-left: 1px #c4c3c5;
    padding: 40px 0 0 0;
        padding-top: 40px;
    text-align: center;
}
.page-account-menu li {
    display: table-cell;
    padding: 10px;
    text-align: center;
    text-transform: uppercase;
    color: #00a3dd;
    border-left: 1px #9b9b9b;
}
}
